import{_ as a,c as e,o as t,Q as i}from"./chunks/framework.1cc28bed.js";const r="/life/img/accident/China_Eastern_Airlines_Flight_5735_flight_path_zh-cn.svg",B=JSON.parse('{"title":"2022年中国东方航空5735号班机空难","description":"","frontmatter":{},"headers":[],"relativePath":"accident/2022-China-Eastern-Airlines-Flight-5735.md","filePath":"accident/2022-China-Eastern-Airlines-Flight-5735.md","lastUpdated":1682564276000}'),n={name:"accident/2022-China-Eastern-Airlines-Flight-5735.md"},l=i('<h1 id="_2022年中国东方航空5735号班机空难" tabindex="-1">2022年中国东方航空5735号班机空难 <a class="header-anchor" href="#_2022年中国东方航空5735号班机空难" aria-label="Permalink to &quot;2022年中国东方航空5735号班机空难&quot;">​</a></h1><p><img src="'+r+'" alt=""></p><p>中国东方航空5735号班机空难，官方名称“3·21”东航MU5735航空器飞行事故，又称3·21东航客机事故、东航“3·21”坠机事故或梧州空难、藤县空难，是发生于北京时间2022年3月21日下午的一起中国客机坠毁事故。</p><p>失事飞机是由东方航空云南有限公司执飞的波音737-800型客机，编号B-1791，自昆明飞往广州，航班号为MU5735（IATA）或CES5735（ICAO）。</p><p>飞机在被监测到快速下降及失联后于当日14时22分左右在广西壮族自治区梧州市藤县埌南镇莫埌村的一处山林坠毁。</p><h2 id="伤亡" tabindex="-1">伤亡 <a class="header-anchor" href="#伤亡" aria-label="Permalink to &quot;伤亡&quot;">​</a></h2><p>涉事航班共载有乘客123人、机组人员9人，均为中华人民共和国公民，涉及中国大陆17个省的74户家庭。</p><p>国家应急处置指挥部于26日晚间举行新闻发布会，确认航班上132人全部遇难。</p><p>救援人员在此之前已发现部分遇难者的遗体残骸。</p><p>截至3月28日，机上132名遇难者DNA比对工作已经完成，遇难者身份已经全部确认。</p><h2 id="事故原因与调查" tabindex="-1">事故原因与调查 <a class="header-anchor" href="#事故原因与调查" aria-label="Permalink to &quot;事故原因与调查&quot;">​</a></h2><p>截至目前，坠毁事故原因仍在调查中</p><h2 id="相关空难" tabindex="-1">相关空难 <a class="header-anchor" href="#相关空难" aria-label="Permalink to &quot;相关空难&quot;">​</a></h2><ul><li><a href="https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9B%BD%E6%B0%91%E8%88%AA3303%E5%8F%B7%E7%8F%AD%E6%9C%BA%E7%A9%BA%E9%9A%BE" target="_blank" rel="noreferrer">中国民航3303号班机空难</a> ，1982年，112死</li><li><a href="https://zh.wikipedia.org/wiki/%E4%B8%AD%E5%9B%BD%E5%8D%97%E6%96%B9%E8%88%AA%E7%A9%BA3943%E5%8F%B7%E7%8F%AD%E6%9C%BA%E7%A9%BA%E9%9A%BE" target="_blank" rel="noreferrer">中国南方航空3943号班机空难</a> ，1992年，141死</li></ul>',14),s=[l];function h(o,_,c,p,d,A){return t(),e("div",null,s)}const f=a(n,[["render",h]]);export{B as __pageData,f as default};
