import{_ as a,c as t,o as s,k as e,a as r}from"./chunks/framework.1cc28bed.js";const x=JSON.parse('{"title":"三元里等鄉痛罵鬼子詞","description":"","frontmatter":{"author":false,"pageInfo":false},"headers":[],"relativePath":"arts/xiwen/三元里等乡痛骂鬼子词.md","filePath":"arts/xiwen/三元里等乡痛骂鬼子词.md","lastUpdated":1681890255000}'),o={name:"arts/xiwen/三元里等乡痛骂鬼子词.md"},n=e("div",{class:"heti heti--vertical"},[e("h1",{id:"三元里等鄉痛罵鬼子詞",tabindex:"-1"},[r("三元里等鄉痛罵鬼子詞 "),e("a",{class:"header-anchor",href:"#三元里等鄉痛罵鬼子詞","aria-label":'Permalink to "三元里等鄉痛罵鬼子詞"'},"​")]),e("p",null,"三元里西村南岸九十餘鄉眾衿耆等，為不共戴天，誓滅英夷事：向來英夷屢不安分，久犯天朝。昔攻沙角砲台，戕害官兵。我皇上深仁，不忍加誅，且示懷柔。乃尚不知感恩，猶复包藏禍心，深入重地，施放火箭，燒害民居，攻及城池，目無大憲。欽差大臣見城廂內外遭殃，議息兵安民，英夷理宜得些好意即休。豈料貪勝，不知輸服，得寸進尺，容縱兵卒，擾亂村莊，搶我耕牛，傷我田禾，壞我祖墳，淫辱婦女，鬼神共怒，天地難容。我等所以奮不顧身，困義律於北門，斬伯麥於南岸。汝等逆黨，試思此際若非我府尊為爾解圍，各逆其能保首領下船乎?今聞爾出示當途，辱罵大憲無功，揚言於眾，總要與伯麥伸冤，視我此地無人實甚。是以飽德之義士僉助兵糧，荷鋤之農夫操戈禦敵，糾壯勇數十萬，何怕英逆之義律不可剪除?水戰陸戰兼能，豈怕夷船堅厚?特使鬼子無隻身存留，鬼船無片帆回國。爾等不避，不日交戰。為此特示。")],-1),_=[n];function c(i,d,l,p,h,f){return s(),t("div",null,_)}const u=a(o,[["render",c]]);export{x as __pageData,u as default};
