import{_ as t,c as s,o as r,k as e,a}from"./chunks/framework.1cc28bed.js";const u=JSON.parse('{"title":"撲滅異端邪教公啟","description":"","frontmatter":{"author":false},"headers":[],"relativePath":"arts/xiwen/扑灭异端邪教公启.md","filePath":"arts/xiwen/扑灭异端邪教公启.md","lastUpdated":1681890255000}'),n={name:"arts/xiwen/扑灭异端邪教公启.md"},o=e("div",{class:"heti heti--vertical"},[e("h1",{id:"撲滅異端邪教公啟",tabindex:"-1"},[a("撲滅異端邪教公啟 "),e("a",{class:"header-anchor",href:"#撲滅異端邪教公啟","aria-label":'Permalink to "撲滅異端邪教公啟"'},"​")]),e("p",null,[a("清朝 "),e("a",{href:"#",title:"1862"},"同治元年")]),e("p",null,"江西闔省士民耆庶，為公立議約事。"),e("p",null,"照得外夷和議，原為通商牟利，我天朝皇帝念其奔走跋涉，曲允其請，以示懷柔遠人之意。乃有奸民羅安當、方安之倡行邪教，煽惑愚民，甚至採生折割，姦淫婦女，錮蔽幼童，行蹤詭秘，殊堪痛憾。本年二月，經闔省義民，齊心拆毀天主教堂，泄我公憤。正欲誅殛羅、方二賊，惜彼先期逃遁。近聞他赴京控訴，慫他國領事官來文，膽敢問我撫台大人，要賠還銀七萬兩，並要城外育嬰堂產業，蓋還城裡原堂。種種無賴，意圖訛詐。目下軍需緊急，我中國金銀豈能填無厭之求？為此遍告同人，共伸義憤。倘該國教士，膽敢來江蠱惑，我等居民數十百萬，振臂一呼，同聲相應。鋤頭扁擔，盡作利兵，白叟黃童，悉成勁旅。務將該邪教斬除淨盡，不留餘孽，殺死一個，償爾一命，殺死十個，償爾十命。其有中國人投習彼教者，經各鄉族長查出，不必稟官，公同處死，以為不敬祖宗甘心從逆者戒。謹此告白。")],-1),i=[o];function l(c,_,d,p,h,f){return r(),s("div",null,i)}const x=t(n,[["render",l]]);export{u as __pageData,x as default};
