import{_ as t,c as e,o as a,Q as l}from"./chunks/framework.1cc28bed.js";const r="/life/img/car/benz_c.jpeg",i="/life/img/car/audi_a4.png",s="/life/img/car/bmw_3.jpeg",d="/life/img/car/bmw_x.png",x=JSON.parse('{"title":"买车吗","description":"","frontmatter":{},"headers":[],"relativePath":"daily/car.md","filePath":"daily/car.md","lastUpdated":1686813266000}'),o={name:"daily/car.md"},n=l('<h1 id="买车吗" tabindex="-1">买车吗 <a class="header-anchor" href="#买车吗" aria-label="Permalink to &quot;买车吗&quot;">​</a></h1><h2 id="开车消耗" tabindex="-1">开车消耗 <a class="header-anchor" href="#开车消耗" aria-label="Permalink to &quot;开车消耗&quot;">​</a></h2><p><strong>停车费</strong>：公司 400，有车位免费，无车位400。无房，月停车费800。<br><strong>油价</strong>：95号，7-10/L<br><strong>油费</strong>：首先要知道油耗，按 100km 80块，每个月按22天工作日，工作日通勤按 20km 里程，一个月 352<br><strong>路费</strong>：河南高速 0.45/km，浙江高速 0.4/km，上海高速 0.6/km，江苏高速 0.45/km，广西高速 0.4/km，广东高速 0.45-0.6/km</p><details class="details custom-block"><summary>油耗</summary><p>≤1.0L/T 平均油耗 10L 以内。1.5T 油耗 8-15L/100km。2.0T 油耗 9-20L/100km。1.5L-NA 自然吸气 6-20L/100km。2.0L-NA 油耗 8-20L/100km。2.5L-NA 油耗 8-25L/100km。这是保守与激烈驾驶的跨度标准，NA 动力汽车极限往往更高，不过实际用车过程中普遍偏低，因为驾驶风格就是那么保守。</p></details><h2 id="买什么车" tabindex="-1">买什么车 <a class="header-anchor" href="#买什么车" aria-label="Permalink to &quot;买什么车&quot;">​</a></h2><blockquote><p>截图来自 2022-11-01 14:00 各大官网</p></blockquote><div class="warning custom-block"><p class="custom-block-title"><a href="http://hz.bendibao.com/traffic/2014129/48928.shtm" target="_blank" rel="noreferrer">杭州快速路限行</a></p></div><h3 id="benz" tabindex="-1">Benz <a class="header-anchor" href="#benz" aria-label="Permalink to &quot;Benz&quot;">​</a></h3><p><img src="'+r+'" alt="benz c"></p><h3 id="audi" tabindex="-1">Audi <a class="header-anchor" href="#audi" aria-label="Permalink to &quot;Audi&quot;">​</a></h3><p><strong>A6L</strong> THE BEST</p><p><img src="'+i+'" alt="audi a4"></p><h3 id="bmw" tabindex="-1">BMW <a class="header-anchor" href="#bmw" aria-label="Permalink to &quot;BMW&quot;">​</a></h3><p><img src="'+s+'" alt="bmw 3"></p><p><img src="'+d+'" alt="bmw x"></p><h2 id="车知识" tabindex="-1">车知识 <a class="header-anchor" href="#车知识" aria-label="Permalink to &quot;车知识&quot;">​</a></h2><h3 id="级别" tabindex="-1">级别 <a class="header-anchor" href="#级别" aria-label="Permalink to &quot;级别&quot;">​</a></h3><p>如今比较广泛的是使用德国的方法来区分汽车的级别，将汽车划分为A00级、A0级、A级、B级、C级、D级。这种分级的方法主要依据汽车轴距、排量、重量等参数划分的。又因为排量你很难看到，除非了解这辆车，而平台则比发动机排量更加难以了解，所以最常见的方法是通过轴距的长度来划分级别。</p><p>两个车轮之间的长度称之为：<strong>轴距</strong> 不是轮距</p><p>通过轴距就可以最为直观的判断一辆车的级别。</p><table><thead><tr><th style="text-align:left;">级别</th><th style="text-align:left;">说明</th><th style="text-align:left;">例子</th></tr></thead><tbody><tr><td style="text-align:left;">微型车(A00级)</td><td style="text-align:left;">通常指轴距在2400mm以下的车型称为微型车</td><td style="text-align:left;">比亚迪F0、吉利熊猫、最经典的奇瑞QQ等</td></tr><tr><td style="text-align:left;">小型车(A0级)</td><td style="text-align:left;">通常指轴距在2400-2550mm之间的车型称为小型车</td><td style="text-align:left;">本田飞度、丰田威驰、福特嘉年华等</td></tr><tr><td style="text-align:left;">紧凑型车(A级)</td><td style="text-align:left;">通常指轴距在2550-2700mm之间的车型称为紧凑型车 这个级别车型是家用轿车的主流车型</td><td style="text-align:left;">大众速腾、丰田卡罗拉、福特福克斯、本田思域等</td></tr><tr><td style="text-align:left;">中型车(B级)</td><td style="text-align:left;">通常指轴距在2700-2850mm之间的车型称为中型车 这个级别车型通常是家用和商务兼用的车型</td><td style="text-align:left;">本田雅阁、丰田凯美瑞、大众迈腾、别克君威等</td></tr><tr><td style="text-align:left;">中大型车(C级)</td><td style="text-align:left;">通常指轴距在2850-3000mm之间的车型称为中大型车 这个级别车型通常是商务用车的主流车型</td><td style="text-align:left;">奥迪A6、宝马5系、奔驰E级、沃尔沃S80等</td></tr><tr><td style="text-align:left;">大型车(D级)</td><td style="text-align:left;">通常指轴距在3000mm以上的车型称为豪华车 这个级别车型通常就是富豪们选择的车型了 价格基本都在百万元以上</td><td style="text-align:left;">奔驰S级、宝马7系、奥迪A8等 他们的轴距通常都在3300mm以上</td></tr></tbody></table><blockquote><p>通常的中大型车轴距都在2900mm左右。但由于国人喜欢更长的轴距，导致轴距都达到了2950mm以上，个别车型例如宝马5系的轴距为3108mm。虽然宝马5系来到中国被拉长了轴距，看起来犹如7系一样，但由于平台定位的缘故，依旧属于中大型车</p></blockquote>',22),m=[n];function c(h,g,p,f,b,_){return a(),e("div",null,m)}const k=t(o,[["render",c]]);export{x as __pageData,k as default};
