import{_ as a,c as i,a3 as t,o as e}from"./chunks/framework.DDs3IadZ.js";const b=JSON.parse('{"title":"软考高级系统架构","description":"","frontmatter":{},"headers":[],"relativePath":"exam/certificate/ruankao-high-lv.md","filePath":"exam/certificate/ruankao-high-lv.md","lastUpdated":1718165336000}'),r={name:"exam/certificate/ruankao-high-lv.md"};function o(h,l,n,d,s,u){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="软考高级系统架构" tabindex="-1">软考高级系统架构 <a class="header-anchor" href="#软考高级系统架构" aria-label="Permalink to &quot;软考高级系统架构&quot;">​</a></h1><p>雨露均沾 需要标记吗🌟</p><p>综合知识 75道选择题，共75分</p><table tabindex="0"><thead><tr><th style="text-align:center;"></th><th style="text-align:left;">科目</th><th style="text-align:left;">占比</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:left;">计算机组成原理与体系结构</td><td style="text-align:left;">2.7%-5.3%</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:left;">系统配置与性能评价</td><td style="text-align:left;">0-2.7%</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:left;">操作系统</td><td style="text-align:left;">8%-10.7%</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:left;">计算机网络</td><td style="text-align:left;">4%-8%</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:left;">数据库系统</td><td style="text-align:left;">5.3%-12%</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:left;">企业信息化战略与实施</td><td style="text-align:left;">4%-10.7%</td></tr><tr><td style="text-align:center;">7</td><td style="text-align:left;">软件工程</td><td style="text-align:left;">16%-24%</td></tr><tr><td style="text-align:center;">8</td><td style="text-align:left;">项目管理</td><td style="text-align:left;">0-5.3%</td></tr><tr><td style="text-align:center;">9</td><td style="text-align:left;">软件架构设计</td><td style="text-align:left;">29.3%-37.3%</td></tr><tr><td style="text-align:center;">10</td><td style="text-align:left;">系统安全性与保密性设计</td><td style="text-align:left;">2.70%</td></tr><tr><td style="text-align:center;">11</td><td style="text-align:left;">法律法规与标准化</td><td style="text-align:left;">4%</td></tr><tr><td style="text-align:center;">12</td><td style="text-align:left;">数学与经济管理</td><td style="text-align:left;">2.70%</td></tr><tr><td style="text-align:center;">13</td><td style="text-align:left;">专业英语</td><td style="text-align:left;">6.67%</td></tr></tbody></table><h2 id="报名" tabindex="-1">报名 <a class="header-anchor" href="#报名" aria-label="Permalink to &quot;报名&quot;">​</a></h2><ul><li><a href="https://www.ruankao.org.cn/" target="_blank" rel="noreferrer">官网</a></li><li><a href="https://bm.ruankao.org.cn/sign/welcome" target="_blank" rel="noreferrer">报名</a></li></ul><div class="tip custom-block"><p class="custom-block-title">机考收费调整</p><p>自2023年下半年起，全国计算机软件资格考试各级别、各资格、各科目考试方式均由纸质笔试改革为计算计化考试（机考），考试时间为11月4日、5日。<br> 收费标准：考试（机考）收费标准按浙价费〔2010〕258号文件规定，初、中级资格考试每人200元，高级资格考试每人300元。</p><p>之前高级收费为210元</p></div><h2 id="计算机组成原理与体系结构" tabindex="-1">计算机组成原理与体系结构 <a class="header-anchor" href="#计算机组成原理与体系结构" aria-label="Permalink to &quot;计算机组成原理与体系结构&quot;">​</a></h2><h3 id="flynn分类法" tabindex="-1">Flynn分类法 <a class="header-anchor" href="#flynn分类法" aria-label="Permalink to &quot;Flynn分类法&quot;">​</a></h3><table tabindex="0"><thead><tr><th>体系结构类型</th><th>结构</th><th>关键特性</th><th>代表</th></tr></thead><tbody><tr><td>SISD单指令流单数据流</td><td>控制部分：一个 <br>处理器：一个<br>主存模块：一个</td><td></td><td>单处理器系统</td></tr><tr><td>SIMD单指令流多数据流</td><td>控制部分：一个 <br>处理器：多个<br>主存模块：多个</td><td></td><td>并行处理机、<strong>阵列处理机</strong>、超级向量处理机</td></tr><tr><td>MISD多指令流多数据流</td><td>控制部分：多个 <br>处理器：一个<br>主存模块：多个</td><td>不实际</td><td>目前没有，文献中有</td></tr><tr><td>MIMD多指令多数据流</td><td>控制部分：多个 <br>处理器：多个<br>主存模块：多个</td><td></td><td>多处理机系统、多计算机</td></tr></tbody></table><h3 id="cisc与risc" tabindex="-1">CISC与RISC <a class="header-anchor" href="#cisc与risc" aria-label="Permalink to &quot;CISC与RISC&quot;">​</a></h3><ul><li>CISC：复杂指令集计算机 Complex Instrcution Set Computers</li><li>RISC：精简指令集计算机 Reduced Instruction Set Computers</li></ul><table tabindex="0"><thead><tr><th style="text-align:center;">指令系统类型</th><th style="text-align:center;">指令</th><th style="text-align:center;">寻址方式</th><th style="text-align:center;">实现方式</th><th style="text-align:center;">其他</th></tr></thead><tbody><tr><td style="text-align:center;">CISC-复杂</td><td style="text-align:center;">数量多，使用频率差别大，可变长格式</td><td style="text-align:center;">支持多种</td><td style="text-align:center;">微程序控制技术</td><td style="text-align:center;">研制周期长</td></tr><tr><td style="text-align:center;">RISC-精简</td><td style="text-align:center;">数量少，使用频率接近，定长格式，大部分为单周期指令，操作寄存器，只有Load/Store操作内存</td><td style="text-align:center;">支持方式少</td><td style="text-align:center;">增加了通用寄存器；应不限逻辑控制为主；适合采用流水线</td><td style="text-align:center;">优化编译，有效支持多种语言</td></tr></tbody></table><h3 id="存储系统" tabindex="-1">存储系统 <a class="header-anchor" href="#存储系统" aria-label="Permalink to &quot;存储系统&quot;">​</a></h3><blockquote><p>重点为磁盘调度算法，存储单元</p></blockquote><h4 id="冯·诺依曼结构" tabindex="-1">冯·诺依曼结构 <a class="header-anchor" href="#冯·诺依曼结构" aria-label="Permalink to &quot;冯·诺依曼结构&quot;">​</a></h4><p>一种将程序指令存储器和数据存储器合并在一起的存储器结构。</p><ul><li>一般用于PC处理器，如i3、i5、i7处理器</li><li>指令与数据存储器合并在一起</li><li>指令与数据都通过相同的数据总线传输</li></ul><h4 id="哈佛结构" tabindex="-1">哈佛结构 <a class="header-anchor" href="#哈佛结构" aria-label="Permalink to &quot;哈佛结构&quot;">​</a></h4><p>一种将程序指令存储和数据存储分开的存储器结构。</p><ul><li>一般用于嵌入式系统处理器DSP(数据信号处理)</li><li>指令与数据分开存储，可以并行读取，有较高数据的吞吐率</li><li>有4条总线：指令和数据的数据总线与地址总线</li></ul><h4 id="层次化存储结构" tabindex="-1">层次化存储结构 <a class="header-anchor" href="#层次化存储结构" aria-label="Permalink to &quot;层次化存储结构&quot;">​</a></h4><ul><li>快-&gt;慢：CPU寄存器、Cache、内存(主存)、外存(辅存)</li><li>局部性原理：访问数据有局部性原理，故可以使用层次化存储结构。</li></ul><h4 id="存储器分类" tabindex="-1">存储器分类 <a class="header-anchor" href="#存储器分类" aria-label="Permalink to &quot;存储器分类&quot;">​</a></h4><ul><li><p>随机存取存储器RAM-Random Access Memory: DRAM-动态存储器 SRAM-静态存储器</p></li><li><p>只读存储器ROM-Read-Only Memory: MROM PROM EPRROM 闪速存储器</p></li><li><p>存储单元指的一行的小单元格</p></li><li><p>编址地址可以按字，也可以按字节8bit</p></li><li><p>存储单元个数=大地址-小地址+1</p></li><li><p>总容量=存储单元个数*编址内容</p></li><li><p>总容量=单元芯片容量*芯片个数</p></li></ul><h4 id="磁盘结构与参数" tabindex="-1">磁盘结构与参数 <a class="header-anchor" href="#磁盘结构与参数" aria-label="Permalink to &quot;磁盘结构与参数&quot;">​</a></h4><p>存取时间=寻道时间+等待时间(平均定位时间+转动延迟)</p><h4 id="磁盘调度算法" tabindex="-1">磁盘调度算法 <a class="header-anchor" href="#磁盘调度算法" aria-label="Permalink to &quot;磁盘调度算法&quot;">​</a></h4><ul><li>FCFS</li><li>SSTF最短寻道时间优先/最短移臂</li></ul><h3 id="流水线技术" tabindex="-1">流水线技术 <a class="header-anchor" href="#流水线技术" aria-label="Permalink to &quot;流水线技术&quot;">​</a></h3><blockquote><p>相关计算：流水线执行时间计算、流水线吞吐率、流水线加速比、流水线效率</p></blockquote><h4 id="流水线" tabindex="-1">流水线 <a class="header-anchor" href="#流水线" aria-label="Permalink to &quot;流水线&quot;">​</a></h4><p>流水线是指在程序执行时多条指令重叠进行操作的一种准并行处理实现技术。各种部件同时处理是针对不同指令而言的，它们可以同时为多条指令的不同部分进行工作，以提高各部件的利用率和指令的平均执行速度。</p><p>--&gt;取指--&gt;分析--&gt;执行</p><ul><li><p>流水线的吞吐率是指单位时间内流水线所完成的任务数量或输出的结果数量。最大吞吐量是极限值。</p></li><li><p>加速比=不使用流水线执行时间/使用流水线执行时间</p></li></ul><h4 id="总线" tabindex="-1">总线 <a class="header-anchor" href="#总线" aria-label="Permalink to &quot;总线&quot;">​</a></h4><p>一条总线同一时刻仅允许一个设备发送，单允许多个设备接受。</p><ul><li>数据总线</li><li>地址总线</li><li>控制总线</li></ul><h3 id="嵌入式系统" tabindex="-1">嵌入式系统 <a class="header-anchor" href="#嵌入式系统" aria-label="Permalink to &quot;嵌入式系统&quot;">​</a></h3><h4 id="芯片" tabindex="-1">芯片 <a class="header-anchor" href="#芯片" aria-label="Permalink to &quot;芯片&quot;">​</a></h4><ul><li>DSP：数字信号处理器，是一种特别适用于进行数字信号处理运算的微处理器。</li><li>Soc：片上系统，是一种有专用目标的集成电路。</li><li>MPU：微机中的中央处理器。构成微机的核心部件。</li><li>MCU：微控制单元，又称单片机/</li></ul><h4 id="嵌入式系统初始化过程" tabindex="-1">嵌入式系统初始化过程 <a class="header-anchor" href="#嵌入式系统初始化过程" aria-label="Permalink to &quot;嵌入式系统初始化过程&quot;">​</a></h4><ul><li>片级初始化：完成嵌入式微处理器的初始化。</li><li>板级初始化：完成嵌入式微处理器以外的其他硬件设备的初始化。</li><li>系统初始化：以软件初始化为主，主要进行操作系统的初始化。</li></ul><h3 id="校验码" tabindex="-1">校验码 <a class="header-anchor" href="#校验码" aria-label="Permalink to &quot;校验码&quot;">​</a></h3><p>校验方法</p><ul><li>奇偶校验码：奇偶校验，可检查1位的错误，不可纠错</li><li>循环冗余校验：在k位信息码之后拼接r位校验码</li><li>海明校验码：可检错，也可纠错。记住位数即可。</li></ul><h3 id="性能指标" tabindex="-1">性能指标 <a class="header-anchor" href="#性能指标" aria-label="Permalink to &quot;性能指标&quot;">​</a></h3><ul><li>字长与数据通路宽度</li><li>主存容量与存取速度</li><li>运算速度</li><li>吞吐量与吞吐率</li><li>响应时间</li><li>与完成时间</li><li>兼容性</li></ul><h3 id="阿姆达尔解决方案" tabindex="-1">阿姆达尔解决方案 <a class="header-anchor" href="#阿姆达尔解决方案" aria-label="Permalink to &quot;阿姆达尔解决方案&quot;">​</a></h3><p>可使用估算法，将整个系统时间设置为10s。</p><h3 id="性能评价方法" tabindex="-1">性能评价方法 <a class="header-anchor" href="#性能评价方法" aria-label="Permalink to &quot;性能评价方法&quot;">​</a></h3><ul><li>时钟频率法</li><li>指令执行速度法</li><li>等效指令速度法</li><li>数据处理速率法</li><li>综合理论性能法</li><li>基准程序法</li></ul><h2 id="操作系统" tabindex="-1">操作系统 <a class="header-anchor" href="#操作系统" aria-label="Permalink to &quot;操作系统&quot;">​</a></h2><h3 id="进程管理" tabindex="-1">进程管理 <a class="header-anchor" href="#进程管理" aria-label="Permalink to &quot;进程管理&quot;">​</a></h3><blockquote><p>PV操作</p></blockquote><p>进程的状态</p><ul><li>就绪-运行-阻塞。阻塞态不能直接到运行态。运行态不能直接到就绪态</li><li>首先考虑非CPU资源</li></ul><p>前趋图</p><ul><li>TODO</li></ul><p>信号量与PV操作</p><ul><li>互斥：会存在一个共享使用的资源，但是某一时刻只能一个进程使用，临界资源的互斥访问</li><li>同步：速度有差异，在一定情况停下等待。</li></ul><p>生产者和消费者就是典型的协同现象。</p><p>银行家算法</p><ul><li>当一个进程对资源的最大需求量不超过系统中的资源数时可以接纳该进程。</li><li>进程可以分期请求资源，但请求的总数不能超过最大需求量</li><li>当系统现有的资源不能满足进程尚需资源数时，对进程的请求可以推迟分配，但总能使进程在有限的时间里得到资源</li></ul><h3 id="存储管理" tabindex="-1">存储管理 <a class="header-anchor" href="#存储管理" aria-label="Permalink to &quot;存储管理&quot;">​</a></h3><blockquote><ul><li>CPU-&gt;Cache-&gt;内存-&gt;外存</li><li>用的时候通过局部性原理支撑，将外存内容调到内存，统称虚拟化存储</li><li>页式存储/段式存储/段页式存储</li></ul></blockquote><p>页式存储组织</p><ul><li>优点：利用率高，碎片小，分配及管理简单</li><li>缺点：增加了系统开销；可能产生抖动现象</li><li>逻辑地址=页号+页内地址</li><li>物理地址=页帧号+页内地址（物理块号又称为页帧块）</li><li>优先淘汰访问位为0，其次考虑修改位为0</li></ul><p>段式存储组织</p><ul><li>按用户作业中的自然段来划分逻辑空间，然后调入内存，段段长度可以不一样。</li><li>优点：多道程序共享内存，各段程序修改互不影响。</li><li>缺点：内存利用率低，内存碎片浪费大。</li><li>逻辑地址：（段号，段内偏移量）</li></ul><p>段页式存储组织</p><ul><li>段式与页式的综合体，先分段，再分页。</li><li>优点：空间浪费小、存储共享容易、存储保护容易、能动态连接</li><li>缺点：由于管理软件的增加，复杂性和开销也随之增加，需要的硬件以及占用的内容也有所增加，使得执行速度大大下降</li></ul><p>快表</p><ul><li>将页表存于Cache上叫快表</li><li>将页表存于内存上叫慢表</li></ul><h3 id="文件管理" tabindex="-1">文件管理 <a class="header-anchor" href="#文件管理" aria-label="Permalink to &quot;文件管理&quot;">​</a></h3><blockquote><p>文件管理的是外存里的文件。</p></blockquote><p>索引文件</p><ul><li>索引可以理解为指针、地址、盘块号</li></ul><p>文件和树型目录结构</p><ul><li>文件属性：R只读文件属性 / A存档属性 / S系统文件 / H隐藏文件</li><li>文件名的组成：驱动器号+路径+主文件名+扩展名</li><li>绝对路径：是从盘符开始的路径</li><li>相对路径：是从当前目录开始的路径</li></ul><p>空闲存储空间的管理</p><ul><li>文件管理：空闲区表法、空闲链表法、位示图法 成组链接法</li></ul><h3 id="作业管理" tabindex="-1">作业管理 <a class="header-anchor" href="#作业管理" aria-label="Permalink to &quot;作业管理&quot;">​</a></h3><blockquote><p>试卷中从未出现过</p></blockquote><h3 id="设备管理" tabindex="-1">设备管理 <a class="header-anchor" href="#设备管理" aria-label="Permalink to &quot;设备管理&quot;">​</a></h3><p>数据传输控制方式</p><ul><li>程序控制方式：严重影响CPU。分为无条件传送和程序查询方式。</li><li>程序中断方式：CPU无需等待而提高了传输请求的响应速度。</li><li>DMA方式：DMA方式比程序控制方式与中断方式都高效。</li><li>通道方式</li><li>I/O处理机</li></ul><p>虚设备与SPOOLING技术</p><ul><li>假脱机技术，没有真正实现脱机打印</li></ul><h2 id="微内核操作系统" tabindex="-1">微内核操作系统 <a class="header-anchor" href="#微内核操作系统" aria-label="Permalink to &quot;微内核操作系统&quot;">​</a></h2><p>单体内核——将图形、设备驱动及文件系统等功能全部在内核中实现，运行在内核状态和统一地址空间。</p><p>微内核——是实现基本功能，将图形系统、文件系统、设备驱动及通信功能放在内核之外。</p><h3 id="嵌入式操作系统" tabindex="-1">嵌入式操作系统 <a class="header-anchor" href="#嵌入式操作系统" aria-label="Permalink to &quot;嵌入式操作系统&quot;">​</a></h3><p>嵌入式操作系统特点</p><ul><li>1.微型化 2.代码质量高 3.专业化 4.实时性强 5.可裁剪、可配置</li><li>实时嵌入式操作系统的内核服务有：异常和中断、计时器、I/O管理</li><li>常见的嵌入式RTOS：VxWorks、RT-Linux、QNX、pSOS</li></ul><h2 id="数据库" tabindex="-1">数据库 <a class="header-anchor" href="#数据库" aria-label="Permalink to &quot;数据库&quot;">​</a></h2><h3 id="数据库模式" tabindex="-1">数据库模式 <a class="header-anchor" href="#数据库模式" aria-label="Permalink to &quot;数据库模式&quot;">​</a></h3><h4 id="三级模式-两层映射" tabindex="-1">三级模式-两层映射 <a class="header-anchor" href="#三级模式-两层映射" aria-label="Permalink to &quot;三级模式-两层映射&quot;">​</a></h4><ul><li>外模式(视图、用户模式、用户视图)-&gt;概念模式(DBA视图)-&gt;内模式(内部试图)</li><li>外模式-概念模式映射 概念模式-内模式映射</li></ul><h4 id="视图" tabindex="-1">视图 <a class="header-anchor" href="#视图" aria-label="Permalink to &quot;视图&quot;">​</a></h4><ul><li>数据库试图是一个虚拟表，其内容由查询定义。但是试图并没有真正存储这些数据，而是通过查询原始表动态生成所需要的数据</li><li>优点：能简化用户操作、使用户能以多角度看待同一数据</li><li>缺点：查询之后再展示效率比较低</li></ul><h4 id="数据库设计过程" tabindex="-1">数据库设计过程 <a class="header-anchor" href="#数据库设计过程" aria-label="Permalink to &quot;数据库设计过程&quot;">​</a></h4><ul><li>需求分析(<strong>数据流图、数据字典</strong>、需求说明书)</li><li>概念结构设计(ER模型)</li><li>逻辑结构设计(关系模式)</li><li>物理设计</li></ul><h3 id="er模型" tabindex="-1">ER模型 <a class="header-anchor" href="#er模型" aria-label="Permalink to &quot;ER模型&quot;">​</a></h3><h4 id="关键" tabindex="-1">关键 <a class="header-anchor" href="#关键" aria-label="Permalink to &quot;关键&quot;">​</a></h4><ul><li>属性</li><li>实体</li><li>联系</li></ul><h3 id="关系代数" tabindex="-1">关系代数 <a class="header-anchor" href="#关系代数" aria-label="Permalink to &quot;关系代数&quot;">​</a></h3><h4 id="关系代数运算" tabindex="-1">关系代数运算 <a class="header-anchor" href="#关系代数运算" aria-label="Permalink to &quot;关系代数运算&quot;">​</a></h4><ul><li>并</li><li>交</li><li>差</li><li>笛卡尔积</li><li>投影</li><li>选择</li><li>自然连接</li></ul><h3 id="规范化理论" tabindex="-1">规范化理论 <a class="header-anchor" href="#规范化理论" aria-label="Permalink to &quot;规范化理论&quot;">​</a></h3><h4 id="价值与用途" tabindex="-1">价值与用途 <a class="header-anchor" href="#价值与用途" aria-label="Permalink to &quot;价值与用途&quot;">​</a></h4><ul><li>非规范化的关系模式：数据冗余、更新异常、插入异常、删除异常</li></ul><h4 id="函数依赖" tabindex="-1">函数依赖 <a class="header-anchor" href="#函数依赖" aria-label="Permalink to &quot;函数依赖&quot;">​</a></h4><ul><li>部分函数依赖：多个元素的主键才有部分函数依赖</li><li>传递依赖</li></ul><h4 id="键" tabindex="-1">键 <a class="header-anchor" href="#键" aria-label="Permalink to &quot;键&quot;">​</a></h4><ul><li>候选键</li><li>主键：唯一标识元组的属性集合</li><li>外键：其他关系的主键</li></ul><h4 id="范式" tabindex="-1">范式 <a class="header-anchor" href="#范式" aria-label="Permalink to &quot;范式&quot;">​</a></h4><ul><li>1NF：属性值都是不可分的原子值</li><li>2NF：消除非主属性对候选键的部分依赖</li><li>3NF：消除非主属性对候选键的传值依赖</li><li>BCNF：消除主属性对候选键的部分和传递依赖，左侧决定因素必须包括候选码</li></ul><h4 id="无损分解" tabindex="-1">无损分解 <a class="header-anchor" href="#无损分解" aria-label="Permalink to &quot;无损分解&quot;">​</a></h4><ul><li>保持函数依赖分解</li><li>无损分解</li></ul><h4 id="反规范化" tabindex="-1">反规范化 <a class="header-anchor" href="#反规范化" aria-label="Permalink to &quot;反规范化&quot;">​</a></h4><ul><li>由于规范化会使得表不断的拆分，从而导致数据表过多，会增加查询的工作量。</li><li>技术手段：增加派生性冗余列、增加冗余列、重新组表、分割表（水平分割、垂直分割）</li></ul><h3 id="并发控制" tabindex="-1">并发控制 <a class="header-anchor" href="#并发控制" aria-label="Permalink to &quot;并发控制&quot;">​</a></h3><h4 id="事务的特性" tabindex="-1">事务的特性 <a class="header-anchor" href="#事务的特性" aria-label="Permalink to &quot;事务的特性&quot;">​</a></h4><ul><li>原子性</li><li>一致性</li><li>隔离型</li><li>持续性</li></ul><h4 id="并发产生的问题" tabindex="-1">并发产生的问题 <a class="header-anchor" href="#并发产生的问题" aria-label="Permalink to &quot;并发产生的问题&quot;">​</a></h4><ul><li>丢失更新</li><li>不可重复读</li><li>脏数据</li></ul><h4 id="封锁协议" tabindex="-1">封锁协议 <a class="header-anchor" href="#封锁协议" aria-label="Permalink to &quot;封锁协议&quot;">​</a></h4><ul><li>S锁：读锁、共享锁</li><li>X锁：写锁、排他锁、独占锁</li><li>一级封锁协议：事务T在修改数据R前必须先对其加X锁，直到事务结束才释放。可防止丢失修改。</li><li>二级封锁协议：一级封锁协议加上事务T在读取数据R之前先对其加S锁，读完后即可释放S锁。可防止丢失修改，还可防止读脏数据。</li><li>三级封锁协议：一级封锁协议加上事务T在读取数据R之前先对其加S锁，直到事务结束才释放。可防止丢失修改，还可防止读脏数据、防止数据重复读。</li><li>两段锁协议：可串行化、可能发生死锁。</li></ul><h3 id="数据库完整性约束" tabindex="-1">数据库完整性约束 <a class="header-anchor" href="#数据库完整性约束" aria-label="Permalink to &quot;数据库完整性约束&quot;">​</a></h3><h4 id="约束" tabindex="-1">约束 <a class="header-anchor" href="#约束" aria-label="Permalink to &quot;约束&quot;">​</a></h4><ul><li>实体完整性约束：主键唯一且为空</li><li>参照完整性约束：外键，其他关键的主键，或者为空</li><li>用户自定义完整性约束</li><li>针对<strong>复杂的约束</strong>，系统提供了<strong>触发器机制</strong>，通过用户编程实现。</li></ul><h3 id="数据库安全性" tabindex="-1">数据库安全性 <a class="header-anchor" href="#数据库安全性" aria-label="Permalink to &quot;数据库安全性&quot;">​</a></h3><ul><li>用户标识与坚定</li><li>存取控制</li><li>密码存储和传输</li><li>视图的保护</li><li>审计</li></ul><h4 id="数据备份" tabindex="-1">数据备份 <a class="header-anchor" href="#数据备份" aria-label="Permalink to &quot;数据备份&quot;">​</a></h4><ul><li>冷备份：数据库在停止状态下，将数据库的文件全部备份下来</li><li>热备份：数据库在运行状态下，将数据库的文件全部备份下来</li><li>完全备份：备份所有数据</li><li>差量备份：仅备份上一次<strong>完全备份</strong>之后变化的数据</li><li>增量备份：备份上一次备份之后变化的数据</li><li>影子拷贝：该策略假设在某一个时刻只有一个活动的事务，首先对数据库做副本（称为影子副本），并在磁盘上维护一个dp_pointer指针，指向数据库的当前副本。对于要执行写操作的数据项，数据库系统在磁盘上维护数据库的一个副本，所有的写操作都在数据库副本上执行，而保持原始数据库不变，如果在任一时刻操作不得不中止，系统仅需要删除新副本，原数据库副本没有受到任何影响。</li><li>基于日志的延迟修改技术（deferred-modification technique）的设计与恢复过程。该技术通过在日志中记录所有对数据库的修改操作，将一个事务的所有写操作延迟到事务提交后才执行，日志中需要记录“事务开始”和“事务提交”时间，还需要记录数据项被事务修改后的新值，无需记录数据项被事务修改前的原始值。当系统发生故障时，如果某个事务已经开始，但没有提交，则该事务对数据项的修改尚未体现在数据库中，因此无需做任何恢复动作。</li></ul><h3 id="分布式数据库" tabindex="-1">分布式数据库 <a class="header-anchor" href="#分布式数据库" aria-label="Permalink to &quot;分布式数据库&quot;">​</a></h3><h4 id="分片透明性" tabindex="-1">分片透明性 <a class="header-anchor" href="#分片透明性" aria-label="Permalink to &quot;分片透明性&quot;">​</a></h4><ul><li>分片透明性</li><li>位置透明性</li><li>局部数据模型透明性</li></ul><h4 id="分布式数据库管理系统ddbms-组成" tabindex="-1">分布式数据库管理系统DDBMS-组成 <a class="header-anchor" href="#分布式数据库管理系统ddbms-组成" aria-label="Permalink to &quot;分布式数据库管理系统DDBMS-组成&quot;">​</a></h4><ul><li>LDBMS（局部数据库管理系统）</li><li>GDBMS（全局数据库管理系统）</li><li>全局数据字典</li><li>通信管理Cm</li></ul><h4 id="分布式数据库管理系统-结构" tabindex="-1">分布式数据库管理系统-结构 <a class="header-anchor" href="#分布式数据库管理系统-结构" aria-label="Permalink to &quot;分布式数据库管理系统-结构&quot;">​</a></h4><ul><li>全局控制集中的DDBMS</li><li>全局控制分散的DDBMS</li><li>全局控制部分分散的DDBMS</li></ul><h4 id="关系数据库与nosql数据库对比" tabindex="-1">关系数据库与NoSQL数据库对比 <a class="header-anchor" href="#关系数据库与nosql数据库对比" aria-label="Permalink to &quot;关系数据库与NoSQL数据库对比&quot;">​</a></h4><table tabindex="0"><thead><tr><th>--</th><th>关系数据库模式</th><th>--</th></tr></thead><tbody><tr><td>并发支持</td><td>支持并发、效率低</td><td>并发性能高</td></tr><tr><td>存储与查询</td><td>关系表方式存储、SQL查询</td><td>海量数据存储、查询效率高</td></tr><tr><td>扩展方式</td><td>向上扩展</td><td>向外扩展</td></tr><tr><td>索引方式</td><td>B树、哈希等</td><td>键值索引</td></tr><tr><td>应用领域</td><td>面向通用领域</td><td>特定应用领域</td></tr></tbody></table><h4 id="nosql" tabindex="-1">NoSQL <a class="header-anchor" href="#nosql" aria-label="Permalink to &quot;NoSQL&quot;">​</a></h4><table tabindex="0"><thead><tr><th>类型</th><th>eg</th></tr></thead><tbody><tr><td>键值</td><td>redis</td></tr><tr><td>列存储数据库</td><td>HBase</td></tr><tr><td>文档型数据库</td><td>MongoDB</td></tr><tr><td>图形数据库</td><td>Neo4j</td></tr></tbody></table><h3 id="数据仓库与数据挖掘" tabindex="-1">数据仓库与数据挖掘 <a class="header-anchor" href="#数据仓库与数据挖掘" aria-label="Permalink to &quot;数据仓库与数据挖掘&quot;">​</a></h3><h2 id="计算机网络" tabindex="-1">计算机网络 <a class="header-anchor" href="#计算机网络" aria-label="Permalink to &quot;计算机网络&quot;">​</a></h2><h3 id="tcp-ip协议族" tabindex="-1">TCP/IP协议族 <a class="header-anchor" href="#tcp-ip协议族" aria-label="Permalink to &quot;TCP/IP协议族&quot;">​</a></h3><h4 id="dhcp与dns" tabindex="-1">DHCP与DNS <a class="header-anchor" href="#dhcp与dns" aria-label="Permalink to &quot;DHCP与DNS&quot;">​</a></h4><ul><li>DHCP：动态主机配置协议</li><li>DNS：域名系统。主机向本地域名服务器的查询采用递归查询（正确答案）。本地域名服务器向根域名服务器的查询通过采用迭代查询（线索）。</li></ul><h4 id="tcp与udp" tabindex="-1">TCP与UDP <a class="header-anchor" href="#tcp与udp" aria-label="Permalink to &quot;TCP与UDP&quot;">​</a></h4><ul><li>TCP：可靠的传输协议</li><li>UDP：不可靠的传输协议</li></ul><h3 id="网络规划与设计" tabindex="-1">网络规划与设计 <a class="header-anchor" href="#网络规划与设计" aria-label="Permalink to &quot;网络规划与设计&quot;">​</a></h3><h4 id="网络规划原则" tabindex="-1">网络规划原则 <a class="header-anchor" href="#网络规划原则" aria-label="Permalink to &quot;网络规划原则&quot;">​</a></h4><ul><li>实用性原则</li><li>开发性原则</li><li>先进性原则</li></ul><h4 id="网络设计任务" tabindex="-1">网络设计任务 <a class="header-anchor" href="#网络设计任务" aria-label="Permalink to &quot;网络设计任务&quot;">​</a></h4><ul><li>确定网络总体目标</li><li>确定总统设计原则</li><li>通信子网设计</li><li>资源子网设计</li><li>设备选型</li><li>网络操作系统与服务器资源设备</li><li>网络安全设计</li></ul><h4 id="网络实施原则" tabindex="-1">网络实施原则 <a class="header-anchor" href="#网络实施原则" aria-label="Permalink to &quot;网络实施原则&quot;">​</a></h4><ul><li>可靠性原则</li><li>安全性原则</li><li>高效性原则</li><li>可扩展性原则</li></ul><h4 id="网络实施步骤" tabindex="-1">网络实施步骤 <a class="header-anchor" href="#网络实施步骤" aria-label="Permalink to &quot;网络实施步骤&quot;">​</a></h4><ul><li>工程实施计划</li><li>网络设备到货验收</li><li>设备安装</li><li>系统测试</li><li>系统试运行</li><li>用户培训</li><li>系统转换</li></ul><h4 id="网络规划与设计阶段" tabindex="-1">网络规划与设计阶段 <a class="header-anchor" href="#网络规划与设计阶段" aria-label="Permalink to &quot;网络规划与设计阶段&quot;">​</a></h4><ul><li>需求规范</li><li>通信规范</li><li>逻辑网络设计🌟</li><li>物理网络设计🌟</li><li>实施阶段</li></ul><h4 id="逻辑设计与物理设计" tabindex="-1">逻辑设计与物理设计 <a class="header-anchor" href="#逻辑设计与物理设计" aria-label="Permalink to &quot;逻辑设计与物理设计&quot;">​</a></h4><ul><li>逻辑网络设计阶段：拓扑结构、网络地址分配、安全规划、网络管理、选择路由协议、设备命名规范</li><li>物理网络设计阶段：设备的具体物理分布、运行环境等确定、设备选型、结构化布线、机房设计</li><li>逻辑网络设计阶段输出：逻辑网络设计图、<strong>IP地址方案</strong>、<strong>安全方案</strong>、招聘和培训网络员工的具体说明、对软硬件、服务、员工和培训费用初步估计</li><li>物理网络设计阶段输出：网络物理结构图和布线方案、<strong>设备和部件的详细列表清单</strong>、软硬件和安装费用的估算、安装日程表、详细说明服务的时间以及期限、安装后的测试计划、用户的培训计划</li></ul><h4 id="分层设计" tabindex="-1">分层设计 <a class="header-anchor" href="#分层设计" aria-label="Permalink to &quot;分层设计&quot;">​</a></h4><ul><li>接入层：用户接入、计费管理、MAC地址认证、收集用户信息</li><li>汇聚层：网络访问策略控制、数据包处理、过滤、寻址</li><li>核心层：高速数据交换、常用冗余机制。在设计核心层设备的功能时，应尽量避免使用数据包过滤、策略路由等降低数 据包转发处理的特性，以优化核心层获得低延迟和良好的可管理性。</li></ul><h3 id="网络接入" tabindex="-1">网络接入 <a class="header-anchor" href="#网络接入" aria-label="Permalink to &quot;网络接入&quot;">​</a></h3><h4 id="有线接入" tabindex="-1">有线接入 <a class="header-anchor" href="#有线接入" aria-label="Permalink to &quot;有线接入&quot;">​</a></h4><ul><li>公用交换电话网络PSTN</li><li>数字数据网DDN</li><li>综合业务数字网ISDN</li><li><strong>非对称数字用户线路ADSL</strong></li><li>同轴光纤技术HFC</li></ul><h4 id="无线接入" tabindex="-1">无线接入 <a class="header-anchor" href="#无线接入" aria-label="Permalink to &quot;无线接入&quot;">​</a></h4><ul><li>IEEE 802.11（WIFI）</li><li>IEEE 802.15（蓝牙）</li><li>红外</li><li>WAPI网络存储</li></ul><h3 id="网络存储技术" tabindex="-1">网络存储技术 <a class="header-anchor" href="#网络存储技术" aria-label="Permalink to &quot;网络存储技术&quot;">​</a></h3><h4 id="存储分类" tabindex="-1">存储分类 <a class="header-anchor" href="#存储分类" aria-label="Permalink to &quot;存储分类&quot;">​</a></h4><ul><li>直连式存储DAS</li><li>网络附加存储NAS</li><li>存储区域网络SAN</li><li>IPSAN</li></ul><h4 id="raid" tabindex="-1">Raid <a class="header-anchor" href="#raid" aria-label="Permalink to &quot;Raid&quot;">​</a></h4><ul><li>Raid0：条块化。性能最高，并行处理，无冗余，损坏无法恢复</li><li>Raid1：镜像结构。可用性，可修复性好，仅有50%利用率</li><li>Raid0+1：高效也可靠</li><li>Raid3：奇偶校验并行传送。N+1模式，有固定的校验盘，坏一个盘可恢复</li><li>Raid5：分布式奇偶校验的独立磁盘。N+1模式，无固定的校验盘，坏一个盘可恢复</li><li>Raid6：两种存储的奇偶校验。N+2模式，无固定的校验盘，坏两个盘可恢复</li></ul><h4 id="ipv6" tabindex="-1">IPv6 <a class="header-anchor" href="#ipv6" aria-label="Permalink to &quot;IPv6&quot;">​</a></h4><ul><li>IPv6长度为128位，地址空间增大了2^96倍</li></ul><h3 id="综合布线系统" tabindex="-1">综合布线系统 <a class="header-anchor" href="#综合布线系统" aria-label="Permalink to &quot;综合布线系统&quot;">​</a></h3><ul><li>工作区子系统</li><li>水平干线系统</li><li>管理间子系统</li><li>垂直干线子系统</li><li>设备间子系统</li><li>建筑群子系统</li></ul><h3 id="物联网" tabindex="-1">物联网 <a class="header-anchor" href="#物联网" aria-label="Permalink to &quot;物联网&quot;">​</a></h3><p>二维码</p><h3 id="云计算" tabindex="-1">云计算 <a class="header-anchor" href="#云计算" aria-label="Permalink to &quot;云计算&quot;">​</a></h3><h4 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h4><ul><li>集合了大量计算机、规模达到成千上万</li><li>多种软硬件技术相结合</li><li>对客户端设备的要求低</li><li>规模化效应</li></ul><h2 id="企业信息化战略与实施" tabindex="-1">企业信息化战略与实施 <a class="header-anchor" href="#企业信息化战略与实施" aria-label="Permalink to &quot;企业信息化战略与实施&quot;">​</a></h2><h3 id="信息与信息化的概念" tabindex="-1">信息与信息化的概念 <a class="header-anchor" href="#信息与信息化的概念" aria-label="Permalink to &quot;信息与信息化的概念&quot;">​</a></h3><h4 id="企业信息化" tabindex="-1">企业信息化 <a class="header-anchor" href="#企业信息化" aria-label="Permalink to &quot;企业信息化&quot;">​</a></h4><p>企业信息化涉及到对企业管理理念的创新，按照市场发展的要求，对企业现有的管理流程重新整合，管理核心从对 <strong>财务和物料</strong> 的管理，转向对 <strong>技术、物资和人力资源</strong> 的管理，并延伸到对企业技术创新、工艺设计、产品设计、生产制造过程的管理，进而还要扩展到对 <strong>客户关系和供应链</strong> 的管理乃至发展到电子商务。</p><h4 id="信息化需求" tabindex="-1">信息化需求 <a class="header-anchor" href="#信息化需求" aria-label="Permalink to &quot;信息化需求&quot;">​</a></h4><p>一般说来，信息化需求包含3个层次，即<strong>战略需求</strong>、<strong>运作需求</strong>和<strong>技术需求</strong>。</p><ol><li>战略需求。组织信息化的目标是提升组织的竞争能力、为组织的可持续发展提供一个支持环境。从某种意义上来说，信息化对组织不仅仅是服务的手段和实现现有战略的辅助工具；信息化可以把组织战略提升到一个新的水平，为组织带来新的发展契机。特别是对于企业，信息化战略是企业竞争的基础。</li><li>运作需求。组织信息化的运作需求是组织信息化需求非常重要且关键的一环，它包含三方面的内容：一是实现信息化战略目标的需要；二是运作策略的需要。三是人才培养的需要。</li><li>技术需求。由于系统开发时间过长等问题在信息技术层面上对系统的完善、升级、集成和整合提出了需求。也有的组织，原来基本上没有大型的信息系统项目，有的也只是一些单机应用，这样的组织的信息化需求，一般是从头开发新的系统。</li></ol><h4 id="企业信息化方法" tabindex="-1">企业信息化方法 <a class="header-anchor" href="#企业信息化方法" aria-label="Permalink to &quot;企业信息化方法&quot;">​</a></h4><p>企业信息化方法主要包括业务流程重构、核心业务应用、信息系统建设、主题数据库、资源管理和人力资本投资方法。不包括组织机构变革。</p><h3 id="信息系统生命周期" tabindex="-1">信息系统生命周期 <a class="header-anchor" href="#信息系统生命周期" aria-label="Permalink to &quot;信息系统生命周期&quot;">​</a></h3><ul><li>立项阶段</li><li>开发阶段：总体规划、系统分析、系统设计、系统实施、系统验收</li><li>运维阶段</li><li>消亡阶段</li></ul><h3 id="信息系统战略规划" tabindex="-1">信息系统战略规划 <a class="header-anchor" href="#信息系统战略规划" aria-label="Permalink to &quot;信息系统战略规划&quot;">​</a></h3><p>企业信息化涉及到对企业管理理念的创新，按照市场发展的要求，对企业现有的管理流程重新整合，管理核心从对（ ）的管理，转向对（ ）的管理，并延伸到对企业技术创新、工艺设计、产品设计、生产制造过程的管理，进而还要扩展到对（ ）的管理乃至发展到电子商务。</p><h3 id="政府信息化与电子政务" tabindex="-1">政府信息化与电子政务 <a class="header-anchor" href="#政府信息化与电子政务" aria-label="Permalink to &quot;政府信息化与电子政务&quot;">​</a></h3><h4 id="电子政务分类" tabindex="-1">电子政务分类 <a class="header-anchor" href="#电子政务分类" aria-label="Permalink to &quot;电子政务分类&quot;">​</a></h4><ul><li>政府对政府</li><li>政务对企业</li><li>政府对公民</li><li>政府对公务员</li></ul><h3 id="企业门户" tabindex="-1">企业门户 <a class="header-anchor" href="#企业门户" aria-label="Permalink to &quot;企业门户&quot;">​</a></h3><h4 id="企业门户分类" tabindex="-1">企业门户分类 <a class="header-anchor" href="#企业门户分类" aria-label="Permalink to &quot;企业门户分类&quot;">​</a></h4><ul><li>企业信息门户EIP</li><li>企业信息门户EKP</li><li>企业应用门户EAP</li><li>垂直门户</li></ul><h3 id="企业应用集成" tabindex="-1">企业应用集成 <a class="header-anchor" href="#企业应用集成" aria-label="Permalink to &quot;企业应用集成&quot;">​</a></h3><p>基于JavaEE平台的基础功能服务构建应用系统时，<strong>JDBC、JCA和 Java IDL</strong> 可用来集成遗产系统。</p><h4 id="共享数据库" tabindex="-1">共享数据库 <a class="header-anchor" href="#共享数据库" aria-label="Permalink to &quot;共享数据库&quot;">​</a></h4><p>共享数据库是一种重要的企业应用集成方式，它通常将应用程序的数据存储在一个共享数据库中，通过制定统一的数据库模式来处理不同应用的集成需求。共享数据库为不同的应用程序提供了统一的数据存储与格式定义，能够在一定程度上缓解数据语义不一致的问题，但无法完全解决该问题。在共享数据库集成中，多个应用程序可能通过共享数据库频繁地读取和修改相同的数据，这会使数据库成为一个性能瓶颈。共享数据库集成方式的一个重要限制来自外部的已封装应用，这些封装好的应用程序只能采用自己定义的数据库模式，调整和集成余地较小。</p><h4 id="企业集成模式" tabindex="-1">企业集成模式 <a class="header-anchor" href="#企业集成模式" aria-label="Permalink to &quot;企业集成模式&quot;">​</a></h4><ul><li>面向信息的集成</li><li>面向过程的集成：强调处理不同应用系统之间的交互逻辑，与核心业务逻辑相分离，并通过不同应用系统之间的协作共同完成某项业务功能。</li><li>面向服务的集成</li></ul><h2 id="软件工程" tabindex="-1">软件工程 <a class="header-anchor" href="#软件工程" aria-label="Permalink to &quot;软件工程&quot;">​</a></h2><h3 id="软件开发方法" tabindex="-1">软件开发方法 <a class="header-anchor" href="#软件开发方法" aria-label="Permalink to &quot;软件开发方法&quot;">​</a></h3><ul><li>原型法</li><li>结构化法</li><li>面向对象方法</li><li>面向服务的方法</li><li>形式化方法</li><li>统一过程方法</li><li>敏捷方法</li><li>基于架构的开发方法ABSD</li></ul><h3 id="软件开发模型" tabindex="-1">软件开发模型 <a class="header-anchor" href="#软件开发模型" aria-label="Permalink to &quot;软件开发模型&quot;">​</a></h3><ul><li>瀑布模型：迭代模型/迭代开发方法</li><li>演化模型：快速应用开发</li><li>增量模型：构件组装模型/基于构建的开发方法</li><li>螺旋模型：统一过程/统一开发方法</li><li>原型模型：敏捷开发方法</li><li>喷泉模型：模型驱动的开发方法</li><li>V模型：基于架构的开发方法</li></ul><h4 id="敏捷方法4大价值观" tabindex="-1">敏捷方法4大价值观 <a class="header-anchor" href="#敏捷方法4大价值观" aria-label="Permalink to &quot;敏捷方法4大价值观&quot;">​</a></h4><p>沟通、简单、反馈、勇气</p><h4 id="敏捷开发方法" tabindex="-1">敏捷开发方法 <a class="header-anchor" href="#敏捷开发方法" aria-label="Permalink to &quot;敏捷开发方法&quot;">​</a></h4><ul><li>极限编程XP：</li><li>水晶方法：</li><li>开放式编码：</li><li>SCRUM：明确定义了的可重复的方法过程。</li><li>功用驱动开发方法FDD：编程开发人员分为两类：首席程序员和“类”程序员</li><li>ASD方法：</li></ul><h4 id="逆向工程" tabindex="-1">逆向工程 <a class="header-anchor" href="#逆向工程" aria-label="Permalink to &quot;逆向工程&quot;">​</a></h4><ul><li>实现级：抽象语法数、符号表、过程</li><li>结构级：程序分量之间相互依赖关系</li><li>功能级：<strong>程序段功能及程序段</strong></li><li>领域级：应用领域概念之间对应关系</li></ul><h3 id="需求工程" tabindex="-1">需求工程 <a class="header-anchor" href="#需求工程" aria-label="Permalink to &quot;需求工程&quot;">​</a></h3><h4 id="需求开发" tabindex="-1">需求开发 <a class="header-anchor" href="#需求开发" aria-label="Permalink to &quot;需求开发&quot;">​</a></h4><ul><li>需求获取</li><li>需求分析</li><li>需求定义</li><li>需求验证</li></ul><h4 id="需求管理" tabindex="-1">需求管理 <a class="header-anchor" href="#需求管理" aria-label="Permalink to &quot;需求管理&quot;">​</a></h4><ul><li>变更控制</li><li>版本控制</li><li>需求跟踪</li><li>需求状态跟踪</li></ul><h4 id="需求分析" tabindex="-1">需求分析 <a class="header-anchor" href="#需求分析" aria-label="Permalink to &quot;需求分析&quot;">​</a></h4><ul><li>状态转换图STD</li><li>数据流图DFD</li><li>数据模型ER图</li><li>数据字典</li><li>UML</li></ul><h4 id="需求变更管理" tabindex="-1">需求变更管理 <a class="header-anchor" href="#需求变更管理" aria-label="Permalink to &quot;需求变更管理&quot;">​</a></h4><ul><li>问题分析和变更描述</li><li>变更分析和成本计算</li><li>变更实现</li></ul><h3 id="系统设计" tabindex="-1">系统设计 <a class="header-anchor" href="#系统设计" aria-label="Permalink to &quot;系统设计&quot;">​</a></h3><h4 id="软件设计" tabindex="-1">软件设计 <a class="header-anchor" href="#软件设计" aria-label="Permalink to &quot;软件设计&quot;">​</a></h4><ul><li>结构设计</li><li>数据设计</li><li>接口设计</li><li>过程设计</li></ul><h4 id="结构化设计" tabindex="-1">结构化设计 <a class="header-anchor" href="#结构化设计" aria-label="Permalink to &quot;结构化设计&quot;">​</a></h4><ul><li>需求设计：数据流图</li><li>概要设计：模块结构图、层次图和HIPO图</li><li>详细设计：程序流程图、伪代码、盒图</li></ul><h4 id="面向对象设计-设计原则" tabindex="-1">面向对象设计-设计原则 <a class="header-anchor" href="#面向对象设计-设计原则" aria-label="Permalink to &quot;面向对象设计-设计原则&quot;">​</a></h4><ul><li>单一职责原则：设计目的单一的类。</li><li>开放-封闭原则：对扩展开放，对修改封闭。</li><li>李氏（Liskov）替换原则：子类可以替换父类。</li><li>依赖倒置原则：要依赖于抽象，而不是具体实现；针对接口编程，不要针对实现编程。</li><li>接口隔离原则：使用多个专门的接口比使用单一的总接口要好。</li><li>组合重用原则：要尽量使用组合，而不是继承关系达到重用目的。</li><li>迪米特（Demeter）原则（最少知识法则）：一个对象应当对其他对象有尽可能少的了解。</li></ul><p>面向对象的设计模型包含以<strong>包图</strong>表示的软件体系结构图，以<strong>交互图</strong>表示的用例实现图，完整精确的类图，针对复杂对象的状态图和用以<strong>描述流程化处理</strong>的活动图等。</p><h4 id="设计模式-创建型模式" tabindex="-1">设计模式-创建型模式 <a class="header-anchor" href="#设计模式-创建型模式" aria-label="Permalink to &quot;设计模式-创建型模式&quot;">​</a></h4><ul><li>Factory Method工厂方法模式：</li><li>Abstract Factory抽象工厂模式：</li><li>Builder构建器模式：</li><li>Prototype原型模式：</li><li>Singleton单例模式：</li></ul><h4 id="设计模式-结构型模式" tabindex="-1">设计模式-结构型模式 <a class="header-anchor" href="#设计模式-结构型模式" aria-label="Permalink to &quot;设计模式-结构型模式&quot;">​</a></h4><ul><li>Adapter适配器模式</li><li>Bridge桥接模式</li><li>Composite组合模式</li><li>Decorator装饰模式</li><li>Fecade外观模式</li><li>Flyweight享元模式：对庞大对象的管控。eg：汉字编码、棋牌。</li><li>Proxy代理模式：快捷方式</li></ul><h4 id="设计模式-行为型模式" tabindex="-1">设计模式-行为型模式 <a class="header-anchor" href="#设计模式-行为型模式" aria-label="Permalink to &quot;设计模式-行为型模式&quot;">​</a></h4><ul><li>Chain of Responsibility职责链模式：eg：请假审批</li><li>Command命令模式：eg：日志记录，可撤销</li><li>Interpreter解释器模式：eg：虚拟机</li><li>Iterator迭代器模式：数据集</li><li>Mediator中介者模式：不直接引用</li><li>Menmento备忘录模式：游戏存档</li><li>Observer观察者模式：订阅、广播、联动</li><li>State状态模式：状态变成类</li><li>Strategy策略模式：多方案切换</li><li>Template Method模版方法模式：框架</li><li>Visitor访问者模式：数据与操作分离。在不改变原来类结构(活动节点)的基础上增加新功能。</li></ul><h3 id="软件测试" tabindex="-1">软件测试 <a class="header-anchor" href="#软件测试" aria-label="Permalink to &quot;软件测试&quot;">​</a></h3><h4 id="软件测试方法" tabindex="-1">软件测试方法 <a class="header-anchor" href="#软件测试方法" aria-label="Permalink to &quot;软件测试方法&quot;">​</a></h4><ul><li>动态测试：计算机运行。黑盒测试、白盒测试、灰盒测试</li><li>静态测试：纯人工。桌前检查、代码审查、代码走查</li></ul><h4 id="测试阶段" tabindex="-1">测试阶段 <a class="header-anchor" href="#测试阶段" aria-label="Permalink to &quot;测试阶段&quot;">​</a></h4><ul><li>单元测试</li><li>集成测试：主要测试各模块之间的接口是否正常起作用。</li><li>系统测试</li><li>确认测试</li><li>回归测试</li></ul><h4 id="软件调试" tabindex="-1">软件调试 <a class="header-anchor" href="#软件调试" aria-label="Permalink to &quot;软件调试&quot;">​</a></h4><ul><li>蛮力法</li><li>回溯法</li><li>原因排除法</li></ul><h4 id="系统测试" tabindex="-1">系统测试 <a class="header-anchor" href="#系统测试" aria-label="Permalink to &quot;系统测试&quot;">​</a></h4><p>系统测试是根据系统方案说明书来设计测试用例，常见的系统测试主要有恢复测试、安全性测试、压力测试、性能测试、可靠性测试、可用性测试、可维护性测试和安装测试。</p><h3 id="软件运行与软件维护" tabindex="-1">软件运行与软件维护 <a class="header-anchor" href="#软件运行与软件维护" aria-label="Permalink to &quot;软件运行与软件维护&quot;">​</a></h3><h4 id="系统转换计划" tabindex="-1">系统转换计划 <a class="header-anchor" href="#系统转换计划" aria-label="Permalink to &quot;系统转换计划&quot;">​</a></h4><ul><li>高水平高价值：改造</li><li>高水平低价值：集成</li><li>低水平高价值：继承</li><li>低水平低价值：淘汰</li></ul><h4 id="软件维护" tabindex="-1">软件维护 <a class="header-anchor" href="#软件维护" aria-label="Permalink to &quot;软件维护&quot;">​</a></h4><ul><li>正确性维护</li><li>适应性维护</li><li>完善性维护</li><li>预防性维护</li></ul><h2 id="项目管理" tabindex="-1">项目管理 <a class="header-anchor" href="#项目管理" aria-label="Permalink to &quot;项目管理&quot;">​</a></h2><h3 id="立项管理" tabindex="-1">立项管理 <a class="header-anchor" href="#立项管理" aria-label="Permalink to &quot;立项管理&quot;">​</a></h3><h4 id="盈亏平衡分析" tabindex="-1">盈亏平衡分析 <a class="header-anchor" href="#盈亏平衡分析" aria-label="Permalink to &quot;盈亏平衡分析&quot;">​</a></h4><ul><li>销售额=固定成本+可变成本+税费+利润【正常情况】</li><li>销售额=固定成本+可变成本+税费【盈亏平衡】</li><li>可变成本/销售额不变，税费/销售额不变</li></ul><h3 id="范围管理" tabindex="-1">范围管理 <a class="header-anchor" href="#范围管理" aria-label="Permalink to &quot;范围管理&quot;">​</a></h3><p>确定项目的边界，即哪些工作是项目应该做的，哪些工作不应该包括在项目中</p><ul><li>范围计划编制</li><li>范围定义：<strong>项目章程/项目范围管理计划/批准的变更申请/组织过程资产</strong></li><li>创建WBS：便于估算，明确范围，防止需求蔓延。WBS字典</li><li>范围确认</li><li>范围控制</li></ul><h3 id="时间管理" tabindex="-1">时间管理 <a class="header-anchor" href="#时间管理" aria-label="Permalink to &quot;时间管理&quot;">​</a></h3><p>时间管理也叫进度管理。</p><ul><li>活动定义</li><li>活动排序</li><li>活动资源估算</li><li>活动历时估算</li><li>指定进度计划</li><li>进度控制</li></ul><h4 id="前导图法-单代号网络图-pdm" tabindex="-1">前导图法（单代号网络图，PDM） <a class="header-anchor" href="#前导图法-单代号网络图-pdm" aria-label="Permalink to &quot;前导图法（单代号网络图，PDM）&quot;">​</a></h4><ul><li>关键路径的长度=最短工期=<strong>最长</strong>路径</li><li>总时差为0的活动为关键活动</li><li>关键活动组成的路径为关键路径</li><li>一个项目可以有多条关键路径，但是对项目不好</li></ul><h4 id="关键路径法" tabindex="-1">关键路径法 <a class="header-anchor" href="#关键路径法" aria-label="Permalink to &quot;关键路径法&quot;">​</a></h4><ul><li>总时差（松弛时间）：在不延误</li><li>自由时差</li></ul><h3 id="成本管理" tabindex="-1">成本管理 <a class="header-anchor" href="#成本管理" aria-label="Permalink to &quot;成本管理&quot;">​</a></h3><h4 id="成本估算方法" tabindex="-1">成本估算方法 <a class="header-anchor" href="#成本估算方法" aria-label="Permalink to &quot;成本估算方法&quot;">​</a></h4><ul><li>自顶向下的估算</li><li>自底向上的估算</li><li>差别估算法</li></ul><h4 id="成本预算方法" tabindex="-1">成本预算方法 <a class="header-anchor" href="#成本预算方法" aria-label="Permalink to &quot;成本预算方法&quot;">​</a></h4><ul><li>直接成本与间接成本</li><li>管理储备</li><li>零基准预算</li></ul><h3 id="软件质量管理" tabindex="-1">软件质量管理 <a class="header-anchor" href="#软件质量管理" aria-label="Permalink to &quot;软件质量管理&quot;">​</a></h3><h4 id="质量保证-质量控制" tabindex="-1">质量保证&amp;质量控制 <a class="header-anchor" href="#质量保证-质量控制" aria-label="Permalink to &quot;质量保证&amp;质量控制&quot;">​</a></h4><ul><li>质量保证：每隔一段时间进行。工具：质量审计和过程分析</li><li>质量控制：实时监控项目的具体结果</li></ul><h4 id="软件过程改进-cmmi" tabindex="-1">软件过程改进 CMMI <a class="header-anchor" href="#软件过程改进-cmmi" aria-label="Permalink to &quot;软件过程改进 CMMI&quot;">​</a></h4><ul><li>已管理级</li><li>已定义级</li><li>定量管理级</li><li>优化级</li></ul><h3 id="软件配置管理" tabindex="-1">软件配置管理 <a class="header-anchor" href="#软件配置管理" aria-label="Permalink to &quot;软件配置管理&quot;">​</a></h3><h4 id="变更控制过程" tabindex="-1">变更控制过程 <a class="header-anchor" href="#变更控制过程" aria-label="Permalink to &quot;变更控制过程&quot;">​</a></h4><ul><li>变更申请</li><li>变更评估</li><li>变更决策</li><li>变更实施</li><li>变更验证</li><li>沟通存档</li></ul><h4 id="软件工具" tabindex="-1">软件工具 <a class="header-anchor" href="#软件工具" aria-label="Permalink to &quot;软件工具&quot;">​</a></h4><ul><li>软件开发工具：需求分析工具、设计工具、编码与排错工具</li><li>软件维护工具：版本控制工具、文档分析工具、开发信息库工具、逆向工程工具、再工程工具</li><li>软件管理和软件支持工具：项目管理工具、配置管理工具、软件评价工具、软件开发工具的评价和选择</li></ul><h4 id="配置项" tabindex="-1">配置项 <a class="header-anchor" href="#配置项" aria-label="Permalink to &quot;配置项&quot;">​</a></h4><ul><li>属于产品组成部分的工作成果：如需求文档、设计文档、源代码和测试用例等；</li><li>属于项目管理和机构支撑过程域产生的文档：如工作计划、项目质量报告和项目跟踪报告等。</li></ul><h2 id="软件架构设计" tabindex="-1">软件架构设计 <a class="header-anchor" href="#软件架构设计" aria-label="Permalink to &quot;软件架构设计&quot;">​</a></h2><h3 id="软件架构的概念" tabindex="-1">软件架构的概念 <a class="header-anchor" href="#软件架构的概念" aria-label="Permalink to &quot;软件架构的概念&quot;">​</a></h3><h4 id="架构的本质" tabindex="-1">架构的本质 <a class="header-anchor" href="#架构的本质" aria-label="Permalink to &quot;架构的本质&quot;">​</a></h4><ul><li>软件架构为软件系统提供了一个结构、行为和属性的高级抽象。</li><li>架构架构风格是特定应用领域的惯用模式，架构定义一个词汇表和一组约束</li></ul><h4 id="架构的-4-1-视图" tabindex="-1">架构的“4+1”视图 <a class="header-anchor" href="#架构的-4-1-视图" aria-label="Permalink to &quot;架构的“4+1”视图&quot;">​</a></h4><ul><li>逻辑视图</li><li>开发视图</li><li>进程视图</li><li>物理视图</li></ul><h3 id="软件架构风格" tabindex="-1">软件架构风格 <a class="header-anchor" href="#软件架构风格" aria-label="Permalink to &quot;软件架构风格&quot;">​</a></h3><h4 id="五大架构风格" tabindex="-1">五大架构风格 <a class="header-anchor" href="#五大架构风格" aria-label="Permalink to &quot;五大架构风格&quot;">​</a></h4><ul><li>数据流风格： <ul><li>批处理：大量<strong>整体</strong>数据、无需用户交互</li><li>管道过滤器：流式数据、弱用户交互</li></ul></li><li>调用/返回风格： <ul><li>主程序/子程序</li><li>面向对象</li><li>层次结构</li></ul></li><li>独立构件风格： <ul><li>进程通信</li><li>事件驱动系统（<strong>隐式调用</strong>）：windows图形界面、断点回调</li></ul></li><li>虚拟机风格： <ul><li>解释器：适用于需要“自定义规则”的场合</li><li>规则系统：适用于专家系统</li></ul></li><li>仓库风格： <ul><li>数据库系统、</li><li>黑板系统：语音识别、知识推理、模式识别、知识推理</li><li>超文本系统</li></ul></li></ul><h4 id="其他风格" tabindex="-1">其他风格 <a class="header-anchor" href="#其他风格" aria-label="Permalink to &quot;其他风格&quot;">​</a></h4><ul><li><strong>闭环控制架构（过程控制）</strong>：适合于嵌入式系统，<strong>适用于处理简单任务</strong>。经验应用：空调控温、定速巡航</li><li>C2风格：构件和连接件都有一个顶部和一个底部</li></ul><h4 id="层次架构风格" tabindex="-1">层次架构风格 <a class="header-anchor" href="#层次架构风格" aria-label="Permalink to &quot;层次架构风格&quot;">​</a></h4><ul><li>两层C/S --&gt; 三层C/S --&gt; 三层B/S --&gt; 混合架构</li><li>MVC架构风格</li><li>MVP架构风格：MVC的变种</li><li>MVVM</li><li>RIA架构风格</li></ul><h4 id="基于服务的架构soa" tabindex="-1">基于服务的架构SOA <a class="header-anchor" href="#基于服务的架构soa" aria-label="Permalink to &quot;基于服务的架构SOA&quot;">​</a></h4><ul><li>服务 &gt; 构件 &gt; 对象</li><li>Web Service</li><li>ESB：提供位置透明性的消息路由和寻址服务</li></ul><h3 id="架构描述语言adl" tabindex="-1">架构描述语言ADL <a class="header-anchor" href="#架构描述语言adl" aria-label="Permalink to &quot;架构描述语言ADL&quot;">​</a></h3><h4 id="adl的三个基本元素" tabindex="-1">ADL的三个基本元素 <a class="header-anchor" href="#adl的三个基本元素" aria-label="Permalink to &quot;ADL的三个基本元素&quot;">​</a></h4><ul><li>构件：计算或数据存储单元</li><li>连接件：用于构件之间交互建模的体系结构构造块及其支配这些交互的规则</li><li>架构配置：描述体系结构的构建与连接件的连接图</li></ul><h3 id="特定领域软件架构dssa" tabindex="-1">特定领域软件架构DSSA <a class="header-anchor" href="#特定领域软件架构dssa" aria-label="Permalink to &quot;特定领域软件架构DSSA&quot;">​</a></h3><h4 id="基本活动" tabindex="-1">基本活动 <a class="header-anchor" href="#基本活动" aria-label="Permalink to &quot;基本活动&quot;">​</a></h4><ul><li>领域分析：建立领域模型</li><li>领域设计：获得DSSA</li><li>领域实现：开发和组织可复用信息</li></ul><h4 id="角色" tabindex="-1">角色 <a class="header-anchor" href="#角色" aria-label="Permalink to &quot;角色&quot;">​</a></h4><ul><li>领域专家：专家只提供意见，不干活</li><li>领域分析人员：有经验的系统分析员</li><li>领域设计人员：有经验的软件设计人员</li><li>领域实现人员：有经验的程序设计人员</li></ul><h3 id="基于架构的软件开发absd" tabindex="-1">基于架构的软件开发ABSD <a class="header-anchor" href="#基于架构的软件开发absd" aria-label="Permalink to &quot;基于架构的软件开发ABSD&quot;">​</a></h3><h4 id="概念" tabindex="-1">概念 <a class="header-anchor" href="#概念" aria-label="Permalink to &quot;概念&quot;">​</a></h4><ul><li>ABSD方法是架构驱动，即强调由业务、质量和功能需求的组合驱动架构设计</li><li>ABSD有三个基础。1.功能的分解。2.通过选择架构风格来实现质量和业务需求。3.软件模板的使用。</li><li>采用<strong>视角与视图</strong>来描述软件架构，采用<strong>用例</strong>来描述功能需求，采用<strong>质量场景</strong>来描述质量需求。</li><li>ABSD 方法是一个自顶向下，递归细化的过程，软件系统的架构通过该方法得到细化，直到能产生软件构件的类。</li></ul><h4 id="开发过程" tabindex="-1">开发过程 <a class="header-anchor" href="#开发过程" aria-label="Permalink to &quot;开发过程&quot;">​</a></h4><ul><li>架构需求</li><li>架构设计</li><li>架构文档化：输出结果为<strong>架构规则说明</strong>和<strong>测试架构需求的质量设计说明书</strong>这两个文档。</li><li>架构复审：目的是<strong>标识潜在的风险</strong>。</li><li>架构实现</li><li>架构演化</li></ul><h3 id="软件质量属性-软件架构评估" tabindex="-1">软件质量属性 &amp; 软件架构评估 <a class="header-anchor" href="#软件质量属性-软件架构评估" aria-label="Permalink to &quot;软件质量属性 &amp; 软件架构评估&quot;">​</a></h3><h4 id="质量属性" tabindex="-1">质量属性 <a class="header-anchor" href="#质量属性" aria-label="Permalink to &quot;质量属性&quot;">​</a></h4><ul><li><strong>性能</strong>：系统的响应能力。增加可用资源/</li><li>可靠性：</li><li><strong>可用性</strong>：系统能正常运行的时间比例。ping/echo/心跳/主动冗余</li><li><strong>安全性</strong>：审计追踪/身份验证</li><li><strong>可修改性</strong>：信息隐藏</li><li>功能性</li><li>可变性</li><li>互操作性</li></ul><h4 id="评估点" tabindex="-1">评估点 <a class="header-anchor" href="#评估点" aria-label="Permalink to &quot;评估点&quot;">​</a></h4><ul><li>敏感点：一个或多个构件的特性</li><li>权衡点：多个质量属性的敏感点</li><li>风险点</li><li>非风险点</li></ul><h4 id="基于场景的评估方法" tabindex="-1">基于场景的评估方法 <a class="header-anchor" href="#基于场景的评估方法" aria-label="Permalink to &quot;基于场景的评估方法&quot;">​</a></h4><ul><li><strong>软件架构分析法SAAM</strong></li><li><strong>架构权衡分析法ATAM</strong>：1.场景和需求收集。2.架构视图和场景实现。3.属性模型构造和分析。4.折中。</li><li>成本效益分析法CBAM</li></ul><h3 id="软件产品线" tabindex="-1">软件产品线 <a class="header-anchor" href="#软件产品线" aria-label="Permalink to &quot;软件产品线&quot;">​</a></h3><h4 id="建立产品线的方式" tabindex="-1">建立产品线的方式 <a class="header-anchor" href="#建立产品线的方式" aria-label="Permalink to &quot;建立产品线的方式&quot;">​</a></h4><ul><li>演化方式</li><li>革命方式</li></ul><h3 id="构件与中间件技术" tabindex="-1">构件与中间件技术 <a class="header-anchor" href="#构件与中间件技术" aria-label="Permalink to &quot;构件与中间件技术&quot;">​</a></h3><h4 id="构建的特性" tabindex="-1">构建的特性 <a class="header-anchor" href="#构建的特性" aria-label="Permalink to &quot;构建的特性&quot;">​</a></h4><ol><li>独立部署单元</li><li>作为第三方的组装单元</li><li>没有（外部的）可见状态</li></ol><h4 id="基于中间件技术的优点" tabindex="-1">基于中间件技术的优点 <a class="header-anchor" href="#基于中间件技术的优点" aria-label="Permalink to &quot;基于中间件技术的优点&quot;">​</a></h4><ol><li>面向需求</li><li>业务的分隔和包容性</li><li>设计与实现隔离</li><li>隔离复杂的系统资源</li><li>符合标准的交互模型</li><li>软件复用</li><li>提供对应用构件的管理</li></ol><h4 id="构件的复用" tabindex="-1">构件的复用 <a class="header-anchor" href="#构件的复用" aria-label="Permalink to &quot;构件的复用&quot;">​</a></h4><ul><li>检索与提取构件</li><li>理解与评论构件</li><li>修改构件</li><li>组装构件</li></ul><h4 id="构件标准" tabindex="-1">构件标准 <a class="header-anchor" href="#构件标准" aria-label="Permalink to &quot;构件标准&quot;">​</a></h4><ul><li>COBRA</li><li>COM</li><li>EJB</li></ul><p>基于构件的开发模型由软件的需求分析定义、体系结构设计、构件库建立、应用软件构建以及测试和发布 5 个阶段组成。</p><h3 id="软件架构文档" tabindex="-1">软件架构文档 <a class="header-anchor" href="#软件架构文档" aria-label="Permalink to &quot;软件架构文档&quot;">​</a></h3><p>软件架构文档的写作应该遵循一定的原则，这些原则包括：</p><ul><li>文档要从<strong>使用者</strong>的角度进行编写；必须分发给所有与系统有关的开发人员：</li><li>应该保持架构文档的即时更新，但更新不要过于频繁：</li><li>架构文档中描述应该尽量避免不必要的重复；</li><li>每次架构文档修改都应该记录进行修改的原则。</li></ul><h2 id="系统安全分析与设计" tabindex="-1">系统安全分析与设计 <a class="header-anchor" href="#系统安全分析与设计" aria-label="Permalink to &quot;系统安全分析与设计&quot;">​</a></h2><h3 id="安全基础技术" tabindex="-1">安全基础技术 <a class="header-anchor" href="#安全基础技术" aria-label="Permalink to &quot;安全基础技术&quot;">​</a></h3><h4 id="对称与非对称加密" tabindex="-1">对称与非对称加密 <a class="header-anchor" href="#对称与非对称加密" aria-label="Permalink to &quot;对称与非对称加密&quot;">​</a></h4><ul><li>对称加密：加密强度不高，但效率高</li><li>密钥分发困难</li></ul><h4 id="常见对称密钥加密算法" tabindex="-1">常见对称密钥加密算法 <a class="header-anchor" href="#常见对称密钥加密算法" aria-label="Permalink to &quot;常见对称密钥加密算法&quot;">​</a></h4><ul><li>DES：替换+移位。三重DES。</li><li>RC-5</li><li>IDEA</li><li>AES</li></ul><p>常见非对称密钥加密算法</p><ul><li>RSA</li><li>Elgamal</li><li>ECC</li></ul><h4 id="信息摘要" tabindex="-1">信息摘要 <a class="header-anchor" href="#信息摘要" aria-label="Permalink to &quot;信息摘要&quot;">​</a></h4><ul><li>保证信息的完整性</li><li><strong>单向散列函数</strong>、固定长度的散列值</li><li>常见的信息摘要算法有MD5、SHA等，市场上广泛使用的MD5，SHA算法的散列值分别为128和160位，由于SHA通用采用的密钥长度较长，因此安全性高于MD5</li></ul><h4 id="数字签名" tabindex="-1">数字签名 <a class="header-anchor" href="#数字签名" aria-label="Permalink to &quot;数字签名&quot;">​</a></h4><ul><li>可以识别发送者身份，防抵赖</li></ul><h4 id="数字证书内容" tabindex="-1">数字证书内容 <a class="header-anchor" href="#数字证书内容" aria-label="Permalink to &quot;数字证书内容&quot;">​</a></h4><ul><li>证书的<strong>版本信息</strong></li><li>证书的<strong>序列号</strong></li><li>证书所使用的签名算法</li><li>证书的发行机构名称</li><li>证书的<strong>有效期</strong></li><li>证书所有人的名称</li><li>证书所有人的<strong>公开密钥</strong></li><li><strong>证书发行者对证书的签名</strong>CA</li></ul><h4 id="pki公钥体系" tabindex="-1">PKI公钥体系 <a class="header-anchor" href="#pki公钥体系" aria-label="Permalink to &quot;PKI公钥体系&quot;">​</a></h4><ul><li>CA认证中心</li><li>RA注册审批机构</li><li>证书受理点</li><li>密钥管理中心-KMC</li></ul><h3 id="网络安全" tabindex="-1">网络安全 <a class="header-anchor" href="#网络安全" aria-label="Permalink to &quot;网络安全&quot;">​</a></h3><h4 id="安全协议" tabindex="-1">安全协议 <a class="header-anchor" href="#安全协议" aria-label="Permalink to &quot;安全协议&quot;">​</a></h4><ul><li><strong>PGP</strong>：优良保密协议。针对邮件和文件的加密系统</li><li>SSL：安全套接字协议。工作在传输层至应用层</li><li>TLS：传输层安全协议。</li><li>SET：安全电子交易协议。电子商务，身份认证</li><li>IPSEC：互联网安全协议。对IP包加密</li></ul><h4 id="网络攻击" tabindex="-1">网络攻击 <a class="header-anchor" href="#网络攻击" aria-label="Permalink to &quot;网络攻击&quot;">​</a></h4><ul><li>被动攻击：收集信息为主。窃听/业务流分析/非法登陆</li><li>主动攻击：假冒身份/抵赖/旁路攻击/重放攻击/拒绝服务</li></ul><h4 id="等级保护标准" tabindex="-1">等级保护标准 <a class="header-anchor" href="#等级保护标准" aria-label="Permalink to &quot;等级保护标准&quot;">​</a></h4><ul><li>用户自主保护级</li><li>系统审计保护级</li><li>安全标记保护级</li><li>结构化保护级</li><li>访问验证保护级</li></ul><h4 id="安全防范体系的层次划分" tabindex="-1">安全防范体系的层次划分 <a class="header-anchor" href="#安全防范体系的层次划分" aria-label="Permalink to &quot;安全防范体系的层次划分&quot;">​</a></h4><ul><li>物理环境的安全性：机房安全</li><li>操作系统的安全性：漏洞补丁管理</li><li>网络的安全性：入侵检测</li><li>应用的安全性：数据库安全</li><li>管理的安全性</li></ul><h2 id="系统可靠性分析与设计" tabindex="-1">系统可靠性分析与设计 <a class="header-anchor" href="#系统可靠性分析与设计" aria-label="Permalink to &quot;系统可靠性分析与设计&quot;">​</a></h2><h3 id="可靠性相关概念" tabindex="-1">可靠性相关概念 <a class="header-anchor" href="#可靠性相关概念" aria-label="Permalink to &quot;可靠性相关概念&quot;">​</a></h3><ul><li>系统可靠性是系统在规定时间内和规定的环境条件下，完成规定功能的能力，也就是系统无故障运行的概率</li><li>系统可用性是指在某个时间点上系统能够按照需求执行的概率</li></ul><h3 id="系统可靠性分析" tabindex="-1">系统可靠性分析 <a class="header-anchor" href="#系统可靠性分析" aria-label="Permalink to &quot;系统可靠性分析&quot;">​</a></h3><ul><li>平均无故障时间</li><li>平均故障修复时间</li><li>平均故障间隔时间</li><li>系统可用性</li></ul><h3 id="软件可靠性设计" tabindex="-1">软件可靠性设计 <a class="header-anchor" href="#软件可靠性设计" aria-label="Permalink to &quot;软件可靠性设计&quot;">​</a></h3><h4 id="影响软件可靠性的主要因素" tabindex="-1">影响软件可靠性的主要因素 <a class="header-anchor" href="#影响软件可靠性的主要因素" aria-label="Permalink to &quot;影响软件可靠性的主要因素&quot;">​</a></h4><ol><li>软件的开发方法和开发环境</li><li>运行环境</li><li>软件规模</li><li>软件内部结构</li><li>软件的可靠性投入</li></ol><h4 id="可靠性设计技术" tabindex="-1">可靠性设计技术 <a class="header-anchor" href="#可靠性设计技术" aria-label="Permalink to &quot;可靠性设计技术&quot;">​</a></h4><ul><li>避错技术</li><li>降低复杂度设计s</li><li>检错技术</li><li>容错技术：N版本程序设计/恢复块设计/防卫式程序设计</li></ul><h2 id="法律法规与标准化知识" tabindex="-1">法律法规与标准化知识 <a class="header-anchor" href="#法律法规与标准化知识" aria-label="Permalink to &quot;法律法规与标准化知识&quot;">​</a></h2><h3 id="保护范围与对象" tabindex="-1">保护范围与对象 <a class="header-anchor" href="#保护范围与对象" aria-label="Permalink to &quot;保护范围与对象&quot;">​</a></h3><table tabindex="0"><thead><tr><th>--</th><th>--</th><th>--</th></tr></thead><tbody><tr><td>著作权法</td><td>著作权/文学、绘画、摄影等作品</td><td>不需要申请，作品完成即开始保护</td></tr><tr><td>软件著作权法</td><td>软件著作权/软件作品</td><td>不需要申请，作品完成即开始保护</td></tr><tr><td>专利法</td><td>专利权</td><td>需要申请，专利权有效期是从申请日开始保护</td></tr><tr><td>商标法</td><td>商标权</td><td>需要申请，核准之日起商标受保护</td></tr><tr><td>反不正当竞争法</td><td>商业秘密权</td><td>商业秘密包括技术与经营两个方面。必须有保密措施才能认定商业秘密</td></tr></tbody></table><h3 id="保护期限" tabindex="-1">保护期限 <a class="header-anchor" href="#保护期限" aria-label="Permalink to &quot;保护期限&quot;">​</a></h3><h3 id="知识产权人确定" tabindex="-1">知识产权人确定 <a class="header-anchor" href="#知识产权人确定" aria-label="Permalink to &quot;知识产权人确定&quot;">​</a></h3><h3 id="侵权判断🌟" tabindex="-1">侵权判断🌟 <a class="header-anchor" href="#侵权判断🌟" aria-label="Permalink to &quot;侵权判断🌟&quot;">​</a></h3><h3 id="标准的分类" tabindex="-1">标准的分类 <a class="header-anchor" href="#标准的分类" aria-label="Permalink to &quot;标准的分类&quot;">​</a></h3><ul><li>国际标准：ISO、IEC</li><li>国家标准：GB-中国、ANSI-美国、BS-英国、JIS-日本</li><li>区域标准：PASC-太平洋地区标准回忆</li><li>行业标准：GJF-中国军用标准</li><li>地方标准：国家的地方一级行政机构制定的标准</li><li>企业标准</li><li>项目规范</li></ul><h3 id="标准代号的识别" tabindex="-1">标准代号的识别 <a class="header-anchor" href="#标准代号的识别" aria-label="Permalink to &quot;标准代号的识别&quot;">​</a></h3><ul><li>国际、国外标准代号：标准代号+专业类号+顺序号+年代号</li><li>我国国家标准代号：强制性标准代号-GB、推荐性标准代号-GB/T、指导性标准代号-GB。Z、实物标准代号GSB</li><li>行业标准代号：汉语拼音大写字母组成</li><li>地方标准代号：由DB加上省级行政区代码的前两位</li><li>企业标准代号：由Q加上企业代号组成</li></ul><h2 id="数学与经济管理" tabindex="-1">数学与经济管理 <a class="header-anchor" href="#数学与经济管理" aria-label="Permalink to &quot;数学与经济管理&quot;">​</a></h2><h3 id="图论应用" tabindex="-1">图论应用 <a class="header-anchor" href="#图论应用" aria-label="Permalink to &quot;图论应用&quot;">​</a></h3><p>做这类题，有一个基本的原则：把多个任务中，第1步耗时最短的安排在最开始执行，再把最后1步耗时最短的安排在最后完成。</p><h4 id="最小生成树" tabindex="-1">最小生成树 <a class="header-anchor" href="#最小生成树" aria-label="Permalink to &quot;最小生成树&quot;">​</a></h4><ul><li>克鲁斯卡尔算法：最短边</li><li>普里斯算法：顶点</li></ul><h4 id="最短路径" tabindex="-1">最短路径 <a class="header-anchor" href="#最短路径" aria-label="Permalink to &quot;最短路径&quot;">​</a></h4><h4 id="网络与最大流量" tabindex="-1">网络与最大流量 <a class="header-anchor" href="#网络与最大流量" aria-label="Permalink to &quot;网络与最大流量&quot;">​</a></h4><h3 id="运筹方法" tabindex="-1">运筹方法 <a class="header-anchor" href="#运筹方法" aria-label="Permalink to &quot;运筹方法&quot;">​</a></h3><h4 id="关键路径法-1" tabindex="-1">关键路径法 <a class="header-anchor" href="#关键路径法-1" aria-label="Permalink to &quot;关键路径法&quot;">​</a></h4><h4 id="线性规划" tabindex="-1">线性规划 <a class="header-anchor" href="#线性规划" aria-label="Permalink to &quot;线性规划&quot;">​</a></h4><h4 id="动态规划" tabindex="-1">动态规划 <a class="header-anchor" href="#动态规划" aria-label="Permalink to &quot;动态规划&quot;">​</a></h4><h4 id="预测与决策" tabindex="-1">预测与决策 <a class="header-anchor" href="#预测与决策" aria-label="Permalink to &quot;预测与决策&quot;">​</a></h4><h2 id="数学建模" tabindex="-1">数学建模 <a class="header-anchor" href="#数学建模" aria-label="Permalink to &quot;数学建模&quot;">​</a></h2><h2 id="专业英语" tabindex="-1">专业英语 <a class="header-anchor" href="#专业英语" aria-label="Permalink to &quot;专业英语&quot;">​</a></h2><h3 id="软件架构重用" tabindex="-1">软件架构重用 <a class="header-anchor" href="#软件架构重用" aria-label="Permalink to &quot;软件架构重用&quot;">​</a></h3><p>软件架构重用是一个解释性、交互式和反复迭代的过程，包括了多项活动。<strong>信息提取</strong>通过分析系统现有设计和实现工件来构造它的模型。其结果用于在后续活动中构造系统的视图。数据库构建活动把模型中包含的<strong>元素和关系</strong>转换为数据库中的标准存储格式。<strong>视图融合</strong>活动包括定义和操作数据库中存储的信息，理顺、加强并建立起元素之间的连接。重构由两个主要活动组成；<strong>可视化和交互</strong>及<strong>模式定义和识别</strong>。前者提供了一种让用户操作架构元素的机制，后者则提供了用于架构重构的设施。</p><blockquote><p>Software architecture reconstruction is an interpretive, interactive, and iterative process including many activities. <strong>Information extraction</strong> involves analyzing a system&#39;s existing design and implementation artifacts to construct a model of it. The result is used in the following activities to construct a view of the system. The database construction activity converts the <strong>elements and relations</strong> contained in the view into a standard format for storage in a database. The <strong>view fusion</strong> activity involves defining and manipulating the information stored in database to reconcile, augment, and establish connections between the elements. Reconstruction consists of two primary activities: <strong>visualization and interaction</strong> and <strong>pattern definition and recognition</strong>. The former provides a mechanism for the user to manipulate architectural elements, and the latter provides facilities for architecture reconstruction.</p></blockquote><p>系统架构是一个系统的一种表示，包含了<strong>功能</strong>到软硬件构件的映射、<strong>软件架构</strong>到硬件架构的映射以及对于这些组件人机交互的关注。也就是说，系统架构关注于整个系统，包括硬件、软件和使用者。软件架构结构根据其所展示元素的广义性质，可以被分为三个主要类别。 1）<strong>模块结构</strong>将决策体现为一组需要被构建或采购的代码或数据单元。 2）<strong>构件连接器结构</strong>将决策体现为系统如何被结构化为一组具有运行时行为和交互的元素。 3）<strong>分配结构</strong>将决策体现为系统如何在其环境中关联到非软件结构，如CPU、文件系统、网络、开发团队等。</p><blockquote><p>A system&#39;s architecture is a representation of a system in which there is a mapping of <strong>functionality</strong> onto hardware and software components, a mapping of the <strong>software architecture</strong> onto the hardware architecture. and a concern for the human interaction with these components. That is, system architecture is concerned with a total system, including hardware. software, and humans. Software architectural structures can be divided into three major categories, depending on the broad nature of the elements they show.1）<strong>Module structures</strong> embody decisions as a set of code or data units that have to be constructed or procured.2）<strong>Component-and-connector structures</strong> embody decisions as to how the system is to be structured as set of elements that have runtime behavior and interactions.3）<strong>Allocation structures</strong> embody decisions as to how the system will relate to non software structures in its environment(such as CPUs, file systems, networks, development teams, etc.).</p></blockquote><p><strong>架构设计</strong>的目标是确定应用软件的哪些部分将分配到何种硬件。识别出正在开发系统的主要软件构件并分配到系统将要运行的硬件构件。所有软件系统可分为四项基本功能。第一项是<strong>数据存储</strong>。大多数信息系统需要数据进行存储并检索，不论是一个小文件，比如一个字处理器产生的一个备忘录，还是一个大型数据库，比如存储一个企业会计记录的数据库。第二项功能是<strong>数据访问逻辑</strong>，处理过程需要访问数据，这通常是指用SQL进行数据库查询。第三项功能是<strong>应用程序逻辑</strong>，这些逻辑通过数据流图，用例和功能需求来记录。第四项功能是表示逻辑，给用户显示信息并接收用户命令。一个系统的三类主要硬件构件是<strong>客户机、服务器和网络</strong>。</p><blockquote><p>The objective of <strong>architecture design</strong> is to determine what parts of the application software will be assigned to what hardware. The major software components of the system being developed have to be identified and then allocated to the various hardware components on which the system will operate. All software systems can be divided into four basic functions. The first is <strong>data storage</strong>. Most information systems require data to be stored and retrieved, whether a small file,such as a memo produced by a word processor, or a large database, such as one that stores an organization’s accounting records.The second function is the <strong>data access logic</strong>, the processing required to access data, which often means database queries in Structured Query Language. The third function is the <strong>application logic</strong>, which is the logic documented in the DFDs, use cases,and functional requirements.The fourth function is the presentation logic,the display of information to the user and the acceptance of the user’s commands.The three primary hardware components of a system are <strong>clients,servers,and network</strong>.</p></blockquote><h2 id="案例专题" tabindex="-1">案例专题 <a class="header-anchor" href="#案例专题" aria-label="Permalink to &quot;案例专题&quot;">​</a></h2><h3 id="案例主题1-软件工程" tabindex="-1">案例主题1：软件工程 <a class="header-anchor" href="#案例主题1-软件工程" aria-label="Permalink to &quot;案例主题1：软件工程&quot;">​</a></h3><ol><li>需求分析</li><li>面向对象设计</li></ol><h4 id="数据流图dfd答题技巧" tabindex="-1">数据流图DFD答题技巧 <a class="header-anchor" href="#数据流图dfd答题技巧" aria-label="Permalink to &quot;数据流图DFD答题技巧&quot;">​</a></h4><ol><li>补充实体</li><li>补充存储</li><li>补充数据流</li><li>补充加工名</li></ol><h4 id="uml图" tabindex="-1">UML图 <a class="header-anchor" href="#uml图" aria-label="Permalink to &quot;UML图&quot;">​</a></h4><ul><li>静态图（结构图） <ul><li>类图</li></ul></li><li>动态图（行为图） <ul><li>用例图</li><li>顺序图：强调时间顺序</li><li>通信图：协作图，强调消息收发关系，而不突出时间顺序</li><li>状态图：状态转换变迁</li><li>活动图：类似程序流程图，并行行为</li></ul></li></ul><h4 id="uml关系" tabindex="-1">UML关系 <a class="header-anchor" href="#uml关系" aria-label="Permalink to &quot;UML关系&quot;">​</a></h4><ul><li><p>依赖关系：一个事物发生影响另一个关系</p></li><li><p>泛化关系：特殊/一般关系</p></li><li><p>关联关系</p><ul><li>聚合关系：整体与部门生命周期不通。eg：汽车和轮子</li><li>组合关系：整体与部分生命周期相同。eg：公司和部门</li></ul></li><li><p>实现关系：接口与类的关系</p></li></ul><h4 id="类的三种类型-每种类型的主要职责" tabindex="-1">类的三种类型&amp;每种类型的主要职责 <a class="header-anchor" href="#类的三种类型-每种类型的主要职责" aria-label="Permalink to &quot;类的三种类型&amp;每种类型的主要职责&quot;">​</a></h4><ul><li>实体类</li><li>控制类</li><li>边界类</li></ul><h3 id="案例主题2-架构设计" tabindex="-1">案例主题2：架构设计 <a class="header-anchor" href="#案例主题2-架构设计" aria-label="Permalink to &quot;案例主题2：架构设计&quot;">​</a></h3><ol><li>软件架构风格</li><li>质量属性与架构评估</li><li>Web架构综合考查</li></ol><h4 id="软件架构风格-1" tabindex="-1">软件架构风格 <a class="header-anchor" href="#软件架构风格-1" aria-label="Permalink to &quot;软件架构风格&quot;">​</a></h4><ul><li>数据流风格： <ul><li>批处理：大量<strong>整体</strong>数据、无需用户交互</li><li>管道过滤器：流式数据、弱用户交互</li></ul></li><li>调用/返回风格： <ul><li>主程序/子程序</li><li>面向对象</li><li>层次结构</li></ul></li><li>独立构件风格： <ul><li>进程通信</li><li>事件驱动系统（<strong>隐式调用</strong>）：windows图形界面、断点回调</li></ul></li><li>虚拟机风格： <ul><li>解释器：适用于需要“自定义规则”的场合</li><li>规则系统：适用于专家系统</li></ul></li><li>仓库风格： <ul><li>数据库系统、</li><li>黑板系统：语音识别、知识推理、模式识别、知识推理</li><li>超文本系统</li></ul></li></ul><h4 id="质量属性-1" tabindex="-1">质量属性 <a class="header-anchor" href="#质量属性-1" aria-label="Permalink to &quot;质量属性&quot;">​</a></h4><ul><li>性能</li><li>可用性</li><li>可修改性</li><li>安全性</li></ul><h4 id="架构评估" tabindex="-1">架构评估 <a class="header-anchor" href="#架构评估" aria-label="Permalink to &quot;架构评估&quot;">​</a></h4><ul><li>敏感点</li><li>权衡点</li><li>风险点</li><li>非风险</li></ul><h4 id="负载均衡" tabindex="-1">负载均衡 <a class="header-anchor" href="#负载均衡" aria-label="Permalink to &quot;负载均衡&quot;">​</a></h4><ul><li>硬件：F5</li><li>软件：LVS、Nginx、HAproxy</li></ul><h4 id="session共享机制" tabindex="-1">session共享机制 <a class="header-anchor" href="#session共享机制" aria-label="Permalink to &quot;session共享机制&quot;">​</a></h4><ol><li>携带session的cookie</li><li>服务器同步session</li><li>将session存入redis</li></ol><h4 id="有状态与无状态" tabindex="-1">有状态与无状态 <a class="header-anchor" href="#有状态与无状态" aria-label="Permalink to &quot;有状态与无状态&quot;">​</a></h4><ul><li>无状态是指对单次请求对处理，不依赖其他请求</li><li>有状态是指自身保存一些数据</li></ul><h4 id="web相关技术" tabindex="-1">web相关技术 <a class="header-anchor" href="#web相关技术" aria-label="Permalink to &quot;web相关技术&quot;">​</a></h4><ul><li>持久化技术-ORM：Hibernate、Mybatis、</li><li>数据库主从分离：主库写、从库写、一主多从</li><li>常用缓存：MemCache、Redis、Squid</li><li>Redis集群分片方式：客户端分片、中间件实现分片、客户端服务端协作分片</li><li>Redis分布式存储方式：主从模式、哨兵模式、集群模式</li><li>Redis数据分片方案：范围分片、哈希分片、一致性哈希分片</li><li>Redis持久化技术：RDB-快照思想、AOF-日志思想</li><li>Redis常用问题：缓存雪崩、缓存穿透、缓存预热</li></ul><h3 id="案例主题3-数据库设计" tabindex="-1">案例主题3：数据库设计 <a class="header-anchor" href="#案例主题3-数据库设计" aria-label="Permalink to &quot;案例主题3：数据库设计&quot;">​</a></h3><ol><li>规范化与反规范化</li><li>分布式数据库</li><li>NoSQL</li><li>内存数据库</li><li>其他数据库扩展知识</li></ol><h4 id="视图的优点" tabindex="-1">视图的优点 <a class="header-anchor" href="#视图的优点" aria-label="Permalink to &quot;视图的优点&quot;">​</a></h4><ol><li>视图能简化用户的操作</li><li>视图机制可以使用户以不同的方式查询同一数据</li><li>视图对数据库重构提供了一定程度的逻辑独立性</li><li>视图可以对机密的数据提供安全保护</li></ol><h4 id="反规范化-1" tabindex="-1">反规范化 <a class="header-anchor" href="#反规范化-1" aria-label="Permalink to &quot;反规范化&quot;">​</a></h4><p>反规范化优点：提高统计、查询效率</p><p>反规范化缺点：增加了数据冗余，浪费存储空间，增、删、改操作的效率降低，可能导致数据不一致，可能产生添加、修改、删除异常</p><ul><li>增加派生性冗余列</li><li>增加冗余列</li><li>重新组表</li><li>分割表</li></ul><h2 id="案例主题4-其他" tabindex="-1">案例主题4：其他 <a class="header-anchor" href="#案例主题4-其他" aria-label="Permalink to &quot;案例主题4：其他&quot;">​</a></h2><ol><li>时间管理与成本管理</li><li>系统安全性</li><li>系统可靠性</li></ol><h2 id="论文写作" tabindex="-1">论文写作 <a class="header-anchor" href="#论文写作" aria-label="Permalink to &quot;论文写作&quot;">​</a></h2><h3 id="考纲" tabindex="-1">考纲 <a class="header-anchor" href="#考纲" aria-label="Permalink to &quot;考纲&quot;">​</a></h3><ol><li>系统建模</li><li>软件架构设计</li><li>系统设计</li><li>分布式系统设计</li><li>系统的可靠性分析与设计</li><li>系统的安全性和保密性设计</li></ol><h3 id="架构真题" tabindex="-1">架构真题 <a class="header-anchor" href="#架构真题" aria-label="Permalink to &quot;架构真题&quot;">​</a></h3><ul><li>架构方向：论软件架构评估</li><li>数据库维度：数据湖/数据分片/数据库</li><li>软件开发维度：软件测试/设计方法/软件开发过程RUP/软件建模</li><li>系统集成：面向服务架构设计</li></ul><h3 id="大纲" tabindex="-1">大纲 <a class="header-anchor" href="#大纲" aria-label="Permalink to &quot;大纲&quot;">​</a></h3><p>摘要：300-320字（包括标点符号）</p><p>正文：2200-2500字</p><ul><li>找准核心论点：5min</li><li>搭建论文框架：10min</li><li>撰写摘要：15min</li><li>正文写作：90min</li></ul><table tabindex="0"><thead><tr><th>基本框架</th><th>内容</th><th>字数</th></tr></thead><tbody><tr><td>摘要</td><td>项目相关背景及主要功能<br>你的岗位及主要职责<br>论文主体内容的总概<br>项目最终的实施效果或你的总结和感悟等</td><td>300-320字</td></tr><tr><td>正文1：项目背景介绍</td><td>项目背景的详细介绍<br>项目开发的原因<br>项目开始的时间、实施周期<br>你的主要岗位职责等</td><td>400字左右</td></tr><tr><td>正文2：过渡内容</td><td>非核心论文问题的回应<br>引出主体内容（核心论文）</td><td>300-400字</td></tr><tr><td><strong>正文3：主题内容</strong></td><td>采用总分式描述：一总对三分<br>可分4个段落</td><td>1000-1500字</td></tr><tr><td>正文4：论文结论</td><td>先分析项目运行效果<br>再总结项目不足<br>最后提出解决思路</td><td>400-600字</td></tr></tbody></table><h3 id="论文主题1-《软件系统架构风格》" tabindex="-1">论文主题1：《软件系统架构风格》 <a class="header-anchor" href="#论文主题1-《软件系统架构风格》" aria-label="Permalink to &quot;论文主题1：《软件系统架构风格》&quot;">​</a></h3><h3 id="论文主题2-《基于架构的软件设计方法》" tabindex="-1">论文主题2：《基于架构的软件设计方法》 <a class="header-anchor" href="#论文主题2-《基于架构的软件设计方法》" aria-label="Permalink to &quot;论文主题2：《基于架构的软件设计方法》&quot;">​</a></h3><h4 id="absd开发阶段" tabindex="-1">ABSD开发阶段 <a class="header-anchor" href="#absd开发阶段" aria-label="Permalink to &quot;ABSD开发阶段&quot;">​</a></h4><ul><li>架构需求</li><li>架构设计</li><li>架构文档化</li><li>架构复审</li><li>架构实现</li><li>架构演化</li></ul><h2 id="论文主题3-《数据库》" tabindex="-1">论文主题3：《数据库》 <a class="header-anchor" href="#论文主题3-《数据库》" aria-label="Permalink to &quot;论文主题3：《数据库》&quot;">​</a></h2><h2 id="论文主题4-《soa在企业集成中的应用》" tabindex="-1">论文主题4：《SOA在企业集成中的应用》 <a class="header-anchor" href="#论文主题4-《soa在企业集成中的应用》" aria-label="Permalink to &quot;论文主题4：《SOA在企业集成中的应用》&quot;">​</a></h2>',462)]))}const q=a(r,[["render",o]]);export{b as __pageData,q as default};
