import{_ as s,c as n,a3 as i,j as r,a as t,G as l,w as e,B as p,o as d}from"./chunks/framework.DDs3IadZ.js";const x=JSON.parse('{"title":"机场评测","description":"","frontmatter":{},"headers":[],"relativePath":"trip/airport.md","filePath":"trip/airport.md","lastUpdated":1717337020000}'),u={name:"trip/airport.md"};function b(m,a,h,f,q,k){const o=p("Badge");return d(),n("div",null,[a[18]||(a[18]=i('<h1 id="机场评测" tabindex="-1">机场评测 <a class="header-anchor" href="#机场评测" aria-label="Permalink to &quot;机场评测&quot;">​</a></h1><p>停止安检前/停止值机前 <strong>30分钟</strong> 到达机场即可，如 17:35 值机截至，17:05 到达机场即可</p><h2 id="其他信息" tabindex="-1">其他信息 <a class="header-anchor" href="#其他信息" aria-label="Permalink to &quot;其他信息&quot;">​</a></h2><h3 id="贵宾厅" tabindex="-1">贵宾厅 <a class="header-anchor" href="#贵宾厅" aria-label="Permalink to &quot;贵宾厅&quot;">​</a></h3><p>非贵宾厅登机流程：到达机场，进入起飞大厅，值机/办理托运，国内/国际安检，候机，登机口刷机票登机，成功登机。<br> 贵宾厅登机流程（安检前的贵宾厅）：到达机场，前台出示身份证打印登机牌/办理托运，进入休息室休息，等待服务人员提醒登机，开始安检，乘坐巴士前往飞机脚下，成功入机。</p><p><strong>安检前的贵宾厅</strong>：直接到机场，找到贵宾厅。会需要你的身份证，服务人员打印登机牌。按服务员所说位置，进去休息，吃喝玩乐。完全不用担心飞机什么时候起飞，服务人员会来叫你去登机。提醒后，回带领你前往专属安检通道，无需排队。安检过后，坐上专属巴士，直达飞机阶梯下。</p><h2 id="机场列表" tabindex="-1">机场列表 <a class="header-anchor" href="#机场列表" aria-label="Permalink to &quot;机场列表&quot;">​</a></h2><h3 id="杭州萧山国际机场" tabindex="-1">杭州萧山国际机场 <a class="header-anchor" href="#杭州萧山国际机场" aria-label="Permalink to &quot;杭州萧山国际机场&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">公共交通时间计算</p><p>现在居住位置到机场，地铁总计耗时至多80分钟<br> 如果出发过早，地铁还没有发车，可以从现在的居住位置打车到文三路，至少需要16分钟<br><strong>文三路</strong> - 机场， <strong>19号线</strong>，包含发车间隔，最多需要45分钟。下地铁后，到达 T2/T3 航站楼大约需要6分钟，到达 T4 大约需要13分钟</p><p><strong>两种方案</strong><br> 只地铁 80分钟 + 10分钟步行，<strong>90m</strong> - 100m<br> 打车+地铁 65分钟 + 10分钟步行，<strong>75m</strong> - 85m</p></div><div class="info custom-block"><p class="custom-block-title">北贵宾区自助餐</p><p>自助餐开餐时间 Buffet Opening Hours<br> 早餐 07:00-09:00<br> 中餐 11:00-13:00<br> 晚餐 17:00-19:00</p></div><details class="details custom-block"><summary>北贵宾区交通方式</summary><p>【浙江空港贵宾公司】北贵宾区交通方式：</p><p>【自驾车】在高德导航软件中搜索“杭州萧山国际机场北贵宾区”后按规划路线前往；或进机场后在空港大道第一个红绿灯路口左转进入航安路，直行至第二个路口右转进入浙航路隧道，出隧道过红绿灯后直行，过岗亭后继续行驶约500米即可到达。</p><p>【P3停车场】乘坐机场摆渡车至T3航站楼出发层下车，进入航站楼乘电梯下至一楼。从14号门出航站楼，右转直行到底，再左转进入北贵宾区。</p><p>【机场大巴】T3航站楼出发层下车，进入航站楼乘电梯下至一楼。从14号门出航站楼，右转直行到底，再左转进入北贵宾区。</p><p>【地铁】出站后，沿3号航站楼的指示方向走，乘扶梯或升降梯到达一楼，过人行横道前往12号门，不要进航站楼，左转沿12-14号门的方向直行到底，再左转进入北贵宾区。</p><p>如有疑问，欢迎致电0571-86665666（工作时间06:30-22:00），我们将竭诚为您服务。祝您旅途愉快！</p></details><h3 id="南京禄口国际机场" tabindex="-1">南京禄口国际机场 <a class="header-anchor" href="#南京禄口国际机场" aria-label="Permalink to &quot;南京禄口国际机场&quot;">​</a></h3>',12)),r("p",null,[a[4]||(a[4]=t("家-杭州东 ")),a[5]||(a[5]=r("strong",null,"地铁",-1)),l(o,{type:"tip"},{default:e(()=>a[0]||(a[0]=[t("30min")])),_:1}),a[6]||(a[6]=t(" 家-杭州西 ")),a[7]||(a[7]=r("strong",null,"地铁",-1)),a[8]||(a[8]=t()),l(o,{type:"tip"},{default:e(()=>a[1]||(a[1]=[t("40min")])),_:1}),a[9]||(a[9]=t("40m")),a[10]||(a[10]=r("br",null,null,-1)),a[11]||(a[11]=t(" 杭州东/杭州西-南京南 ")),a[12]||(a[12]=r("strong",null,"高铁",-1)),a[13]||(a[13]=t()),l(o,{type:"tip"},{default:e(()=>a[2]||(a[2]=[t("1h30min")])),_:1}),a[14]||(a[14]=r("br",null,null,-1)),a[15]||(a[15]=t(" 南京南-禄口机场 ")),a[16]||(a[16]=r("strong",null,"S1机场线",-1)),a[17]||(a[17]=t()),l(o,{type:"tip"},{default:e(()=>a[3]||(a[3]=[t("40min")])),_:1})]),a[19]||(a[19]=i('<h3 id="南阳姜营机场" tabindex="-1">南阳姜营机场 <a class="header-anchor" href="#南阳姜营机场" aria-label="Permalink to &quot;南阳姜营机场&quot;">​</a></h3><p>机场交通配套很差劲，原本以为跟高铁站面对面，去现场看了之后发现是背靠背，极其不方便，不推荐这个机场。</p><h3 id="上海浦东国际机场" tabindex="-1">上海浦东国际机场 <a class="header-anchor" href="#上海浦东国际机场" aria-label="Permalink to &quot;上海浦东国际机场&quot;">​</a></h3><p>浦东的交通配套不是很方便，从虹桥出发需要预留至少3小时，浪费时间。<br> 如果从杭州出发，在2023年7月及之前的时候，时间/金钱成本都很高。</p><ul><li>机场大巴：武林门/杭州东站-上海浦东，2h30m ￥130</li><li>高铁到虹桥需要转地铁，高铁￥75，地铁￥8，时间1h+1.5h</li></ul><p>期待 2024年 浦东通高铁吧。不要考虑这个机场，如果不在浦东附近的话。</p><h3 id="上海虹桥国际机场" tabindex="-1">上海虹桥国际机场 <a class="header-anchor" href="#上海虹桥国际机场" aria-label="Permalink to &quot;上海虹桥国际机场&quot;">​</a></h3><h3 id="广州白云国际机场" tabindex="-1">广州白云国际机场 <a class="header-anchor" href="#广州白云国际机场" aria-label="Permalink to &quot;广州白云国际机场&quot;">​</a></h3><p>离市区非常远，期待高铁吧，到广州南站地铁需要 80 分钟</p><h3 id="沈阳桃仙国际机场" tabindex="-1">沈阳桃仙国际机场 <a class="header-anchor" href="#沈阳桃仙国际机场" aria-label="Permalink to &quot;沈阳桃仙国际机场&quot;">​</a></h3><h3 id="南宁吴圩国际机场" tabindex="-1">南宁吴圩国际机场 <a class="header-anchor" href="#南宁吴圩国际机场" aria-label="Permalink to &quot;南宁吴圩国际机场&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">贵宾厅指南</p><p>吴圩机场高铁站出站，沿着T2方向直走，到尽头上扶梯/直梯到1F，出电梯沿着西南方向走几步就到 V2 贵宾厅。无正餐，零食饮品泡面不限量。</p></div><h3 id="桂林两江国际机场" tabindex="-1">桂林两江国际机场 <a class="header-anchor" href="#桂林两江国际机场" aria-label="Permalink to &quot;桂林两江国际机场&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">贵宾厅指南</p><p>贵宾厅不在候机楼，在机场外面。走到一楼八号口，右手边。就可以看到逸飞祥贵宾厅。无正餐，零食饮品泡面不限量。</p></div><h3 id="贵阳龙洞堡国际机场" tabindex="-1">贵阳龙洞堡国际机场 <a class="header-anchor" href="#贵阳龙洞堡国际机场" aria-label="Permalink to &quot;贵阳龙洞堡国际机场&quot;">​</a></h3><h3 id="郑州新郑国际机场" tabindex="-1">郑州新郑国际机场 <a class="header-anchor" href="#郑州新郑国际机场" aria-label="Permalink to &quot;郑州新郑国际机场&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">贵宾厅指南</p><p>【河南机场集团】郑州新郑国际机场T2航站楼贵宾B区。<br> 路线一:<br> 如您自驾或有专车送行，请您沿机场迎宾大道最右侧一层地面车道（通道提示牌：贵宾A/B区）按照路标提示行至贵宾B区停车场。【<a href="https://m.iqiyi.com/v_sap8mip98w.html" target="_blank" rel="noreferrer">行车路线视频</a>】<br> 路线二:<br> 如您乘坐地铁或城铁，请您从T2航站楼乘坐直梯，到达至航站楼4层出发厅2号门“紫悦·精选河南名小吃”餐厅附近，乘电梯至一楼贵宾B区，联系电话58518188。<br> 温馨提示：为了您的出行方便，如携带三超行李（超大、超件、超重），建议您提前咨询航司客服。</p></div>',17))])}const P=s(u,[["render",b]]);export{x as __pageData,P as default};
