import{_ as r,c as e,a3 as t,o as l}from"./chunks/framework.DDs3IadZ.js";const u=JSON.parse('{"title":"境外银行卡办理指南","description":"","frontmatter":{},"headers":[],"relativePath":"trip/card.md","filePath":"trip/card.md","lastUpdated":1713014998000}'),i={name:"trip/card.md"};function o(s,a,n,h,c,d){return l(),e("div",null,a[0]||(a[0]=[t('<h1 id="境外银行卡办理指南" tabindex="-1">境外银行卡办理指南 <a class="header-anchor" href="#境外银行卡办理指南" aria-label="Permalink to &quot;境外银行卡办理指南&quot;">​</a></h1><h2 id="储蓄卡" tabindex="-1">储蓄卡 <a class="header-anchor" href="#储蓄卡" aria-label="Permalink to &quot;储蓄卡&quot;">​</a></h2><h3 id="汇丰-hk" tabindex="-1">汇丰 HK <a class="header-anchor" href="#汇丰-hk" aria-label="Permalink to &quot;汇丰 HK&quot;">​</a></h3><p><strong>汇丰 ONE</strong><br> 去当地开户即可，需要排队很久，不过只要能取到号就可以，一定会给你办下来的。但是要等很久，我等了2个半小时，手续比较多。需要带<strong>身份证+通行证+入关凭条</strong>。</p><p>其他账户，需要理财资产大于50万港币或者等值外币</p><p>开好，汇丰 ONE 之后，可以在线申请<strong>万事达借记卡</strong></p><details class="details custom-block"><summary>修改个人信息</summary><p>如果您满足以下3个条件，可通过网上理财更改个人信息：</p><ul><li>已激活您的保安编码器或流动保安编码</li><li>可通过网上理财管理您的账户或信用卡</li><li>在银行登记了有效的手机号码和电邮地址</li></ul><p>通过网上理财只需几个步骤，就可随时更新您的个人资料，十分方便。请您准备好保安编码器或流动保安编码，然後按照以下步骤更改您的通讯地址：</p><ul><li>登录网上理财</li><li>在主页顶部选单选择“理财”</li><li>在“个人设置”下选择“更新个人资料”</li><li>在“更新您的住址、办事处及其他通讯地址”处选择“是”</li><li>填写网上的申请表格</li></ul><p>请留意：</p><ul><li>如需更改指定账户或信用卡的通讯地址，请在“第三步 - 设立新地址为您的户口通讯地址”下选择对应账户或信用卡。</li><li>如需更改非固定格式的地址，请选择“请点选，若您的地址在香港特区以外”。</li><li>如需更改您的保管箱、保险和强积金资料，请选择“保管箱/保险/强积金/其他”下的“是”</li></ul></details><h3 id="中国银行-hk" tabindex="-1">中国银行 HK <a class="header-anchor" href="#中国银行-hk" aria-label="Permalink to &quot;中国银行 HK&quot;">​</a></h3><p>去当地开户即可。需要带<strong>身份证+通行证+入关凭条</strong>。也可内地<a href="https://www.bochk.com/m/sc/crossborder/personal/financialservicehk/account.html" target="_blank" rel="noreferrer">见证开户</a>，但内地见证只能办<strong>留学见证</strong>。<a href="https://www.bochk.com/dam/more/BOC_list_SC.pdf" target="_blank" rel="noreferrer">内地见证网点</a>，提前给银行打电话询问是否可以见证，此表中大多数银行不支持见证的。</p><h3 id="n26" tabindex="-1">N26 <a class="header-anchor" href="#n26" aria-label="Permalink to &quot;N26&quot;">​</a></h3><h2 id="信用卡" tabindex="-1">信用卡 <a class="header-anchor" href="#信用卡" aria-label="Permalink to &quot;信用卡&quot;">​</a></h2><h3 id="汇丰-hk-1" tabindex="-1">汇丰 HK <a class="header-anchor" href="#汇丰-hk-1" aria-label="Permalink to &quot;汇丰 HK&quot;">​</a></h3><p>在线申请即可，需要香港本地居民或者存款超过10万港币或者等值外币</p>',13)]))}const m=r(i,[["render",o]]);export{u as __pageData,m as default};
