import{_ as a,c as e,o as r,Q as t}from"./chunks/framework.1cc28bed.js";const i="/life/img/trip/hgspb.jpg",p="/life/img/trip/xiaoqikong.jpg",b=JSON.parse('{"title":"贵州","description":"","frontmatter":{},"headers":[],"relativePath":"trip/china/guizhou/index.md","filePath":"trip/china/guizhou/index.md","lastUpdated":1688473045000}'),o={name:"trip/china/guizhou/index.md"},l=t('<h1 id="贵州" tabindex="-1">贵州 <a class="header-anchor" href="#贵州" aria-label="Permalink to &quot;贵州&quot;">​</a></h1><h2 id="贵阳" tabindex="-1">贵阳 <a class="header-anchor" href="#贵阳" aria-label="Permalink to &quot;贵阳&quot;">​</a></h2><p>黄果树瀑布，建议夏天去，6-8月，水多。大巴过去。凉粉好吃。</p><p><img src="'+i+'" alt="1"></p><p>青岩古镇，猪蹄好吃。</p><p>荔波，荔波小七孔，也是大巴去的。</p><p><img src="'+p+'" alt="1"></p><p>二七路小吃街。二七路臭豆腐好吃，烤洋芋。那个什么素的卷饼别吃，不好吃，好像叫红汤丝娃娃，就一薄饼卷一堆胡萝卜丝啥的。</p><p>凯里酸汤鱼不好吃，可以试试。花溪牛肉粉。</p><p>天河潭</p><ul><li>花果园湿地公园</li><li>黔灵山公园</li><li>南明河畔的甲秀楼</li></ul><h3 id="计划" tabindex="-1">计划 <a class="header-anchor" href="#计划" aria-label="Permalink to &quot;计划&quot;">​</a></h3><details class="details custom-block"><summary>夭折的计划</summary><p>4.29 4.30 5.1 5.2 5.3</p><p>天气 阴雨晴 广州、广西 23-30<br> 温州 18-25<br> 贵阳 15-26</p><p>4.29 广州，15点落地，15点半出机场，去广州塔，登塔吗？16:30到广州塔。</p><p>3.5h</p><p>22点前要到机场，注意时间。</p><p>飞柳州，</p><p>4.30 柳州中午起床，去吃个饭。晚上23:00火车柳州出发，次日凌晨5点到贵阳，带一堆行李，去哪？</p><p>5.1 去黄果树瀑布吧 5.2 就随便看看 回家了</p><p>5.1 小七孔，因为凌晨就到了可以直接大巴过去，贵阳站-小七孔仅路途3h<br> 5.2 黄果树瀑布，贵阳站-瀑布仅路途2h，下午16:00需要在机场，飞温州</p><p>住宿<br> 4.29晚住柳州<br> 4.30晚住火车<br> 5.1 晚住贵阳<br> 5.2 晚住温州</p></details><h2 id="refer" tabindex="-1">refer <a class="header-anchor" href="#refer" aria-label="Permalink to &quot;refer&quot;">​</a></h2><ul><li><a href="https://www.hgscn.com" target="_blank" rel="noreferrer">黄果树瀑布官网</a></li><li><a href="http://www.liboxiaoqikong.com" target="_blank" rel="noreferrer">小七孔官网</a></li></ul>',15),s=[l];function n(h,c,d,_,m,u){return r(),e("div",null,s)}const g=a(o,[["render",n]]);export{b as __pageData,g as default};
