import{_ as r,c as l,j as e,a as t,o as n}from"./chunks/framework.DDs3IadZ.js";const f=JSON.parse('{"title":"台灣人民抗戰檄文","description":"","frontmatter":{"author":"李自成"},"headers":[],"relativePath":"xiwen/台湾人民抗战檄文.md","filePath":"xiwen/台湾人民抗战檄文.md","lastUpdated":1718273525000}'),s={name:"xiwen/台湾人民抗战檄文.md"};function i(o,a,d,c,p,_){return n(),l("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"台灣人民抗戰檄文",tabindex:"-1"},[t("台灣人民抗戰檄文 "),e("a",{class:"header-anchor",href:"#台灣人民抗戰檄文","aria-label":'Permalink to "台灣人民抗戰檄文"'},"​")]),e("p",null,[t("李自成 "),e("a",{href:"#",title:"1895-05"},"光緒二十一年四月")]),e("p",null,"痛哉！吾台民，從此不得爲大清國之民也！吾大清國皇帝何嘗棄吾台民哉！有賊臣焉，大學士李鴻章也，刑部尚書孫毓汶也，吏部侍郎徐用儀也。台民與汝李鴻章、孫毓汶、徐用儀有何仇乎?大清國列祖列宗與汝有何仇乎？太后、皇上與汝有何仇乎？汝既將發祥之地、陵寢迫近之區割媚倭奴、祖宗有知，其謂我太后、皇上何？尚且不足以快汝意，又將關係七省門戶之臺灣，海外二百餘年戴天不二之臺灣，列祖列宗深仁厚澤不使一夫失所之臺灣，全輸之倭奴！我台民非不能毀家紓難也，我台民非不能親上死長也，我台民非如李鴻章、孫毓汶、徐用儀無廉恥，賣國固位，得罪於天地祖宗也。我台民父母妻子、田廬墳墓、生理家產、身家性命，非喪於倭奴之手，實喪於賊臣李鴻章、孫毓汶、徐用儀之手也。"),e("p",null,"我台民窮無所之，憤無所洩，不能呼號於列祖列宗之靈也，又不能哭訴於太后、皇上之前也。均之死也，爲國家除賊臣而死，尚得爲大清國之雄鬼也矣！我台民與李鴻章、孫毓汶、徐用儀，不共戴天，無論其本身，其子孫，其伯叔兄弟侄，遇之船車街道之中客棧衙署之內，我台民族出一丁，各懷手槍一桿，快刀一柄，登時悉數殲除，以謝天地祖宗、太后皇上，以嘗台民父母妻子、田廬墳墓、生理家產、（身家）性命；無冤無仇，受李鴻章、孫毓汶、徐用儀之毒害，以爲天下萬世無廉無恥、賣國固位、得罪天地祖宗之炯戒。"),e("p",null,"除京都及各省碼頭自行刊刻告白外，凡有血氣者，恐未周知。貴報館食毛踐土有年，主持公論有年，向爲我台民所欽佩。茲奉上《申報》、《滬報》、《新聞報》刊資各四元，請爲連日用大文字刊登報首。亂臣賊子，人人得而誅之，聖訓詔然。貴報館如一一照登，我台民有一線生機，必圖銜報；如將賊臣名字隱諱，我台民快刀手槍具在，必將所以待李鴻章、孫毓汶、徐用儀者，轉而相待。生死呼吸，無怪鹵莽，貴報館諒之。"),e("p",null,"大清光緒二十一年（1895年）四月 臺灣省誓死不與賊臣俱生之臣民公啓")],-1)]))}const h=r(s,[["render",i]]);export{f as __pageData,h as default};
