import{_ as t,c as l,j as e,a as n,o as r}from"./chunks/framework.DDs3IadZ.js";const f=JSON.parse('{"title":"寧波眾義民公啟","description":"","frontmatter":{"author":false},"headers":[],"relativePath":"xiwen/宁波众义民公启.md","filePath":"xiwen/宁波众义民公启.md","lastUpdated":1718273525000}'),s={name:"xiwen/宁波众义民公启.md"};function o(i,a,d,p,c,_){return r(),l("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"寧波眾義民公啟",tabindex:"-1"},[n("寧波眾義民公啟 "),e("a",{class:"header-anchor",href:"#寧波眾義民公啟","aria-label":'Permalink to "寧波眾義民公啟"'},"​")]),e("p",null,"1842"),e("p",null,"眾義民公啟各鄉里父老："),e("p",null,"自去年八月至今，紅毛鬼子佔據寧波、鎮海、定海三城，四出騷擾，燒毀房屋，抄掠銀錢，姦淫婦女，強奪牲畜，使我人民不得安居，生者流離，死者暴露，一切耕種買賣皆不能做，其慘毒不可勝言。雖有官兵前來征剿，路途遙遠，來齊一時不能取勝，將來勢必添兵，再與打仗。總是百姓不能安居，生者流離，死者暴露。"),e("p",null,"不如我們大家公議，各自為主，或一人而聚數十人，或一人而聚數百人，以至數千人，或數万人，愈多愈好，或用暗計，或用明攻，總要把紅毛夷人除滅，不在浙省滋鬧，我等方能享太平之福。此系義舉，但求安靜，不要圖功，務須合力同心，定期起事。"),e("p",null,"此外尚有無父無君之漢奸，被鬼子用錢哄買，聽其使喚，可恨已極！此輩畜生，我等密為訪拿，送官究治；如敢拒捕，公同處死。至如食物等物，亦應公禁，倘私賣於鬼子，皆作漢奸論。"),e("p",null,"義切同仇，憤不能忍，特此告白。")],-1)]))}const m=t(s,[["render",o]]);export{f as __pageData,m as default};
