import{_ as t,c as l,j as e,a as n,o as r}from"./chunks/framework.DDs3IadZ.js";const m=JSON.parse('{"title":"就陸海軍大元帥職宣言","description":"","frontmatter":{"author":"孙中山","date":"1917-09-10T00:00:00.000Z"},"headers":[],"relativePath":"xiwen/就陆海军大元帅职宣言.md","filePath":"xiwen/就陆海军大元帅职宣言.md","lastUpdated":1718273525000}'),s={name:"xiwen/就陆海军大元帅职宣言.md"};function o(i,a,d,_,p,c){return r(),l("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"就陸海軍大元帥職宣言",tabindex:"-1"},[n("就陸海軍大元帥職宣言 "),e("a",{class:"header-anchor",href:"#就陸海軍大元帥職宣言","aria-label":'Permalink to "就陸海軍大元帥職宣言"'},"​")]),e("p",null,"孫中山 1917-09-10"),e("p",null,"昔胡清失道，人心思漢，文與海內志士合謀征討。武昌倡義，黃陂實為主帥。江南方定，共和初造，則南都武昌為中區。嗣以胡運告終，授之袁氏。文雖自退讓，而推蔗非人，終於反噬。南方塗炭，元勳殺戮，國會解散，恣睢五稔，僭號稱帝，實賴西南豪傑出師致討，兵未渡江，元兇殂殞。黃陂以副貳之位，依法繼任。然後知神器不可以力爭，民意不可以橫誣也。徒以除惡未盡，權奸當道，簾孽縱而不治，元勳抑而不用，怏怏之威，上陵元首，詐取之謀，南暨吳蜀，侵約法宣戰媾和之權，辱國會神聖立法之地。既被罷黜，嗾賊興戎，以學解散國會之禍；小醜乘之，應機復辟，民國根本，掃地無馀。猶幸共和大義浹於人心，舉國同聲誓殲元惡。張紹會丁槐等，實受黃陂密令，倡義討逆，師期漏洩，為凶人所掩，乘間攘竊，飾功取威。既覆孱胡，亦以是黜黃陂之命，數遣狙擊，逼迫臥寢，糾合無賴，劫奪印璽，以自成偽政府。譬爾朱榮高歡輩，互為首尾，盜取國柄，其罪均也。"),e("p",null,"文於是時，身在海隅，兵符不屬，會海軍總長程璧光奉命南來，共商大計。既遣兵輪赴秦皇島奉迎黃陂，亦不能致。猶謂人心思順，必有投袂而起者，遷延旬月，寂然無聞。是用崎嶇奔走，躬赴廣州。所賴海軍守正，南紀扶義，知民權之不可泯沒，元首之不可棄遺，姦兇篡竊之不可無對抗，國際交涉之不可無代表也。於是申請國會，集於斯地，間關開議，以文為海陸軍大元帥，責以勘定內亂，恢復約法；奉迎元首之事。文忝為首建之人，謬膺澄清之責，敢謂神州之廣，無有豪傑先我而起也哉！徒以身為與共和生死相繫，黃陂為同建國之人，於義猶一體也。生命傷而手足折，何痛如之！"),e("p",null,"艱難之際，不敢以謙讓自潔，即於6年（1917年）9月10日就職。"),e("p",null,"冀二三君子同德協力，共赴大義。"),e("p",null,"文雖衰老，猶當搴裳濡足，為士卒先，與天下共擊廢總統者！")],-1)]))}const f=t(s,[["render",o]]);export{m as __pageData,f as default};
