import{_ as t,c as r,j as e,a as n,o as s}from"./chunks/framework.DDs3IadZ.js";const m=JSON.parse('{"title":"普告同胞檄稿","description":"","frontmatter":{"author":false},"headers":[],"relativePath":"xiwen/普告同胞檄稿.md","filePath":"xiwen/普告同胞檄稿.md","lastUpdated":1718273525000}'),l={name:"xiwen/普告同胞檄稿.md"};function o(i,a,d,c,p,_){return s(),r("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"普告同胞檄稿",tabindex:"-1"},[n("普告同胞檄稿 "),e("a",{class:"header-anchor",href:"#普告同胞檄稿","aria-label":'Permalink to "普告同胞檄稿"'},"​")]),e("p",null,"嗟夫！我父老子弟，其亦知今日之時勢，爲如何之時勢乎？"),e("p",null,"其亦知今日之時勢，有不容不革命者乎？歐風美雨，澎湃逼人，滿賊漢姦，網羅交至，我同胞處於四面楚歌聲裏，猶不自知，此某等爲大義之故，不得不愷切勸諭者也。夫魚遊釜底，燕處焚巣，旦夕偸生，不自知頻於危殆，我同胞其何以異是耶？財政則婪索無厭，雖負盡納稅義務，而不與人以參政之權；民生則道路流離，而彼方昇平歌舞。侈言立憲，而專制乃得實行；名爲集權，則漢人盡遭剝削。南北兵權既純操於滿奴之手；天下財賦，又欲集之一隅。練兵也，加賦也，種種剝奪，括以一言，制我漢族之死命而已。夫閉關之世，猶不容有一族偏枯之弊，況四鄰逼處，彼乃舉其防家賊、媚異族之手段，送我大好河山？嗟夫！我父老子弟，盍亦一念祖宗基業之艱難、子孫立足之無所，而深思於滿奴之政策耶？"),e("p",null,"某等眷懷祖國之前程，默察天下之大勢，知有不容己於革命，用是張我旗鼓，殲彼醜奴，爲天下創。義旗指處，是我漢族，應表同情也。")],-1)]))}const h=t(l,[["render",o]]);export{m as __pageData,h as default};
