import{_ as r,c as l,j as e,a as t,o as n}from"./chunks/framework.DDs3IadZ.js";const f=JSON.parse('{"title":"李自成檄明臣庶文","description":"","frontmatter":{"author":"李自成"},"headers":[],"relativePath":"xiwen/李自成檄明臣庶文.md","filePath":"xiwen/李自成檄明臣庶文.md","lastUpdated":1718273525000}'),s={name:"xiwen/李自成檄明臣庶文.md"};function i(o,a,d,c,p,_){return n(),l("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"李自成檄明臣庶文",tabindex:"-1"},[t("李自成檄明臣庶文 "),e("a",{class:"header-anchor",href:"#李自成檄明臣庶文","aria-label":'Permalink to "李自成檄明臣庶文"'},"​")]),e("p",null,[t("李自成 "),e("a",{href:"#",title:"1644"},"大順永昌元年")]),e("p",null,"上帝鑑觀，實惟求瘼。下民歸往，只切來蘇。命既靡常，情尤可見。粵稽往代，爰知得失之由序；鑑往識今，每悉治忽之故。"),e("p",null,"諮爾明朝，久席泰宁，寖弛綱紀。君非甚暗，孤立而煬蔽恆多；臣盡行私，比黨而公忠絕少。甚至賄通宮府，朝端之威福日移；利擅宗紳，閭左之脂膏罄竭。公侯皆食肉紈絝，而恃為腹心；宦官悉齕糠犬豚，而藉其耳目。獄囚累累，士無報禮之心；徵斂重重，民有偕亡之恨。肆昊天既窮乎仁愛，致兆民爰苦於災祲。"),e("p",null,"朕起布衣，目擊憔悴之形，身切痌瘝之痛。念茲普天率土，咸罹困窮；詎忍易水燕山，未蘇湯火。躬於恆冀，綏靖黔黎。"),e("p",null,"猶慮爾君爾臣，未達帝心，未喻朕意。是以質言正告：爾能體天念祖，度德審幾，朕將加惠前人，不吝異數。如杞如宋，享祀永延，用彰爾之孝；有室有家，民人胥慶，用彰爾之仁。凡茲百工，勉保乃闢，綿商孫之厚祿，賡嘉客之休聲。克殫厥猷，臣誼靡忒。")],-1)]))}const h=r(s,[["render",i]]);export{f as __pageData,h as default};
