import{_ as t,c as l,j as e,a as n,o as r}from"./chunks/framework.DDs3IadZ.js";const m=JSON.parse('{"title":"楊復光露布獻捷文","description":"","frontmatter":{"author":"杨复光"},"headers":[],"relativePath":"xiwen/杨复光露布献捷文.md","filePath":"xiwen/杨复光露布献捷文.md","lastUpdated":1718273525000}'),s={name:"xiwen/杨复光露布献捷文.md"};function o(i,a,p,d,c,_){return r(),l("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"楊復光露布獻捷文",tabindex:"-1"},[n("楊復光露布獻捷文 "),e("a",{class:"header-anchor",href:"#楊復光露布獻捷文","aria-label":'Permalink to "楊復光露布獻捷文"'},"​")]),e("p",null,"楊復光 883"),e("p",null,"天下兵馬都監押楊復光露布獻捷於行在，陳破賊事狀曰："),e("p",null,"頃者妖興霧市，盜嘯叢祠，而岳牧籓侯，備盜不謹。謂大同之運，常可容姦；謂無事之秋，縱其長惡。賊首黃巢，因得充盈窟穴，蔓延萑蒲，驅我蒸黎，徇其凶逆。展鉏鶴以成鋒刃，殺耕牛以恣燔砲，魑魅晝行，虺蜴夜噬。自南海失守，湖外喪師，養虎災深，馴梟逆大，物無不害，惡靡不為，豺狼貽朝市之憂，瘡磐及腹心之痛。遂至毒流萬姓，盜污兩京。衣冠銜塗炭之悲，郡邑起丘墟之嘆。萬方共怒，十道齊攻，伏九廟之威靈，殄積年之凶醜。"),e("p",null,"河中節度使王重榮神資壯烈，天付機謀，誓立功名，志安家國。至於屯田待敵，率士當衝，收百姓十萬餘家，降賊黨三萬餘眾。法當持重，功遂晚成，久稽原野之刑，未快雷霆之怒。自收同、華，逼近京師，夕烽高照於國門，遊騎俯臨於灞岸。既知四隅斷絕，百計奔衝，如窮鳥觸籠，似飛蛾赴燭。"),e("p",null,"雁門節度使李克用神傳將略，天付忠貞，機謀與武藝皆優，臣節共本心相稱。殺賊無非手刃，入陣率以身先，可謂雄才，得名飛將。自統本軍南下，與臣同力前驅，雖在寢餐，不忘寇孽。"),e("p",null,"今月八日，遣衙隊前鋒楊守宗、河中騎將白志遷、橫野軍使滿存、躡雲都將丁行存、朝邑鎮將康師貞、忠武黃頭軍使龐從等三十都，隨李克用自光泰門先入京師，力摧兇寇。又遣河中將劉讓、王環、冀君武、孫珙，忠武將喬從遇，鄭滑將韓從威，荊南將申屠悰，滄州將賈滔，易定將張仲慶，壽州將張行方，天德將顧彥朗，左神策弩手甄君楚、公孫佐，橫衝軍使楊守亮，躡雲都將高周彝，忠順都將胡真，絳州監軍毛宣伯、聶弘裕等七十都繼進。賊尚為堅陣，來抗官軍。雁門李克用率勵驍雄，整齊金革，叫噪而聲將動瓦，喑嗚而氣欲吞沙，寬列戈矛，密張羅網。於是麾軍背擊，分騎橫衝，日明而劍躍飛輪，風急而旗開走電。使賊如浪，便可塞流；使賊如山，亦須折角。蹂踐則橫屍入地，騰凌則積血成塵，不煩即墨之牛，若駕昆陽之象。楊守宗等齊驅直入，合勢夾攻，從卯至申，群凶大潰。自望春宮前蹙殺，至昇陽殿下攻圍，戈不濫揮，矢無虛發。其賊一時奔走，南入商山，徒延漏刃之生，佇作飲頭之器。"),e("p",null,"自收平京闕，二面皆立大功，若破敵摧兇，李克用實居其首。其餘將佐，同效驅馳。兼臣所部領萬餘人，數歲櫛風沐雨。既茲平盪，並錄以聞。")],-1)]))}const f=t(s,[["render",o]]);export{m as __pageData,f as default};
