import{_ as r,c as n,j as e,a as t,o as s}from"./chunks/framework.DDs3IadZ.js";const h=JSON.parse('{"title":"白朗告示","description":"","frontmatter":{"author":false},"headers":[],"relativePath":"xiwen/白朗告示.md","filePath":"xiwen/白朗告示.md","lastUpdated":1718273525000}'),l={name:"xiwen/白朗告示.md"};function i(o,a,d,c,p,_){return s(),n("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"白朗告示",tabindex:"-1"},[t("白朗告示 "),e("a",{class:"header-anchor",href:"#白朗告示","aria-label":'Permalink to "白朗告示"'},"​")]),e("p",null,[e("a",{href:"#",title:"1914年3月16日"},"中華民國3年"),t("于河南省荊紫關")]),e("p",null,"中原扶漢軍大都督白，為佈告事："),e("p",null,"照得我國自改革以來，神奸主政，民氣不揚，雖托名共和，實勵行專制。本都督輟耕面太息者久之，用是糾合豪杰，為民請命。惟起事之初，無地可據，無餉可資，無軍可恃，東馳西突，為地方累，此亦時勢之無可如何，當亦爾商民人等所共知共諒者也。往歲大軍過境，未嘗過於傷害，爾商民等輸助義餉，似亦粗知大義。大都督深為嘉慰。不料狼心狗肺，以德為怨。迨大軍去後，我軍土之陣亡掩埋玆土者，乃競髮塚開棺，剝衣焚屍，實屬慘無人道。已不以人道待人，而欲人以人道待之，難矣。此次之來，我軍士皆痛心疾首，感欲鏟除，寸草不留，以泄前憤。經本大都督再三誥誡，始獲保全爾等生命。僅焚燒房屋，以示薄懲。夫雨露所以施恩，雷霆所以示威，不有雷霆之威不知雨露之恩，爾商民等思孽由自作，無徒致怨本大部督為也。嗣後本軍過境，爾商民等但能簞壺迎師，不抗不逃，本大都督亦予以一律保護，決不燒殺。仰即通知。此佈。")],-1)]))}const m=r(l,[["render",i]]);export{h as __pageData,m as default};
