import{_ as t,c as r,j as e,a as n,o as s}from"./chunks/framework.DDs3IadZ.js";const f=JSON.parse('{"title":"諭巴蜀檄","description":"","frontmatter":{"author":"司马相如"},"headers":[],"relativePath":"xiwen/谕巴蜀檄.md","filePath":"xiwen/谕巴蜀檄.md","lastUpdated":1718273525000}'),o={name:"xiwen/谕巴蜀檄.md"};function i(l,a,d,c,p,_){return s(),r("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"諭巴蜀檄",tabindex:"-1"},[n("諭巴蜀檄 "),e("a",{class:"header-anchor",href:"#諭巴蜀檄","aria-label":'Permalink to "諭巴蜀檄"'},"​")]),e("p",null,"漢·司馬相如"),e("p",null,"「告巴蜀太守，蠻夷自擅，不討之日久矣。時侵犯邊境， 勞士大夫。陛下即位，存撫天下，集安中國，然後興師 出兵，北征匈奴，單于怖駭，交臂受事，屈膝請和，康居 西域，重譯納貢，稽首來享。移師東指，閩越相誅；右弔 番禺，太子入朝。南夷之君，西僰之長，常效貢職，不敢 怠惰，延頸舉踵，喁喁然皆鄉風慕義，欲為臣妾。道里」 遼遠，山川阻深，不能自致。夫不順者巳誅，而為善者 未賞，故遣中郎將往賓之，發巴蜀之士各五百人以 奉幣衛。使者不然，靡有兵革之事，戰𩰚之患。今聞其 乃發軍興制，驚懼子弟，憂患老長，郡又擅為轉粟運 輸，皆非陛下之意也。當行者或亡，迯自賊殺，亦非人 臣之節也。夫邊郡之士，聞熢舉燧燔，皆攝弓而馳，荷 兵而走，流汗相屬，惟恐居後，觸白刃，冒流矢，議不反 顧，計不旋踵，人懷怒心，如報私讎。彼豈樂死惡生，非 編列之民，而與巴蜀異主哉？計遠慮深，急國家之難， 而樂盡人臣之道也。故有剖符之封，析圭而爵，位為 通侯，居列東第，終則遺顯號於後世，傳土地於子孫， 行事甚忠敬，居位甚「安逸，名聲施於無窮，功烈著而 不滅。是以賢人君子，肝腦塗中原，膏液潤野草而不 辭也。今奉幣使至南夷，即自賊殺，或亡逃扺誅，身死 無名，諡為至愚，恥及父母，為天下笑。人之度量相越， 豈不遠哉！然此非獨行者之罪也，父兄之教不先，子 弟之率不謹，寡廉鮮恥，而俗不長厚也。其被刑戮，不 亦宜」乎！陛下患使者有司之若彼，悼不肖愚民之如 此，故遣信使曉諭百姓，以發卒之事，因數之以不忠 死亡之罪，讓三老孝弟以不教誨之過。方今田時，重 煩百姓，已親見近縣，恐遠所谿谷山澤之民不遍聞， 檄到，亟下縣道，咸諭陛下意，毋忽。")],-1)]))}const h=t(o,[["render",i]]);export{f as __pageData,h as default};
