import{_ as t,c as r,j as e,a as n,o as s}from"./chunks/framework.DDs3IadZ.js";const f=JSON.parse('{"title":"隋文帝伐陳檄","description":"","frontmatter":{"author":"隋文帝杨坚"},"headers":[],"relativePath":"xiwen/隋文帝伐陈檄.md","filePath":"xiwen/隋文帝伐陈檄.md","lastUpdated":1718273525000}'),l={name:"xiwen/隋文帝伐陈檄.md"};function o(i,a,d,c,p,_){return s(),r("div",null,a[0]||(a[0]=[e("div",{class:"heti heti--vertical"},[e("h1",{id:"隋文帝伐陳檄",tabindex:"-1"},[n("隋文帝伐陳檄 "),e("a",{class:"header-anchor",href:"#隋文帝伐陳檄","aria-label":'Permalink to "隋文帝伐陳檄"'},"​")]),e("p",null,"隋文帝楊堅 589年"),e("p",null,"原校雲：悅，西魏永熙三年卒。隋伐陳，以侯莫、陳穎為行軍總管，悅當作穎。梅鼎祚雲：《隋書》穎本傳，穎從秦王俊出魯山道伐陳。又《秦王俊傳》：俊為山南道行軍元帥，督三十總管屯漢口，為上流節度。則此檄稱寡人乃秦王俊，如晉王廣檄陳江總等稱寡人也。今以屬秦王，然不曉何人筆耳。 ）"),e("p",null,"夫文軌未同，江湖致阻，風云密邇，良用依然。肇歲猶寒，想如宜也。經始戎務，念以勞懷。寡人肅禀天策，爰徵不庭，懸旌秣馬，今次楓岸。夫時有盛衰，運有興滅。積德必慶延後嗣，長惡則祚不及遠。陳氏往因際會，竊有金陵，頊承立，遽相屠害。叔寶嗣偽，彌复忍虐。沈酗日增，內荒愈甚。雕牆峻宇，加錦繡於土木；嚴刑酷法，陷人物於塗炭。諫士喪身，元良箝口。無道之極，自古罕聞。有一於此，何可不滅？斯皆公等所悉，素匪寓言。我皇誕膺靈命，光臨大寶，再闢乾坤，重懸日月。歷代逋誅之寇，曠古不羈之民，感我仁風，咸沐至道。唯彼吳越，猶未革心。主上義存字小，含以寬大，冀能撫肓彼民，守其封域。而窮凶極暴，日就月將，士庶無聊，人神共憤。乞師繼軌，獻款相尋，愍彼黎元，實興我役。已有別詔，止廢偽主之身；此外士民，咸從蕩滌。西自巫峽，東達滄海，巨艦覆波濤，樓船出雲霧。瓜步、六合，當腹心之衝，渚宮、漢口，據上游之勢，曲江鎮揚，據其要津，鐵馬介夫，千群萬隊，攘袂扼腕，唯所用之。彼國兵士，多少備見，以寡禦眾，理在非敵，救首救尾，將何以及？況復士無鬥心，人懷二志，身雖擐甲，情在倒戈。假使穰苴更生，孫武猶在，亦不能施其智略，況異昔日者乎？往者呂梁之師，一舉殲滅；淮南之地，二旬悉平。彼之將帥，皆奔走之馀，何可用也！世公早涉戎旅，備經徵行，安危之勢，如近目前，得喪之機，若觀掌內。以國家今日之盛，偽主若猶可全，為其展效，容复可爾？江東難立，有識同知，陳氏必亡，賢愚共見。天之所廢，誰能興之？既無所成，徒自傾覆。夫毒蛇螫手，壯士斷腕，豈不惜其肌骨？所存者大也。公若轉禍為福，因機立功，翻然奮飛，共弘大業，則江東士眾，免於鋒刃之苦，天下生民，欣然太平之世。公當位極台鼎，福延子孫，爰及宗族，咸加榮寵。豈與夫不識天命，拒我王師，舉死扶傷，履涉膏血，同年共日而語哉！斯皆肝膈至言，成敗大理。幸非可惑，宜早圖之。")],-1)]))}const h=t(l,[["render",o]]);export{f as __pageData,h as default};
